/*******************************************************************************
 * @description    : 리스트 조회 - 이벤트
*******************************************************************************/
function div_search_btn_search_OnClick(obj)
{

	if(!f_validSearch()) return;

	ds_list.Clear();

	var sdt = div_search.cal_ifDtmFrom.Value.Replace("-", "") + "000000";
	var edt = div_search.cal_ifDtmTo.Value.Replace("-", "") + "235959";

	// 넘길 파라메터 설정
	cf_setParam(ds_condition, "sl_dt_from"   , sdt); 	// 처리기간1
	cf_setParam(ds_condition, "sl_dt_to"     , edt); 	// 처리기간2
	cf_setParam(ds_condition, "sl_item"      , ""); 	// 처리구분

	// 트랜잭션 설정
	var sSvcID = "svcBosList";
	var sInDs  = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_list=List";
	var sArg   = "sktst.sal.BOS#getOnlineSaleList";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);
}

/*******************************************************************************
 * @description    : 콜백
 * @param sSvcID   : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{	
	switch(sSvcID)
	{
		case "svcCrypto" :
		 alert("암호화 작업이  완료 되었습니다.");
		 break;
		 case "svcInputCrypto" :
		 alert("암호화 작업이  완료 되었습니다.");
		 break;
		 case "svcSalInputCrypto" :
		 alert("암호화 작업이  완료 되었습니다.");
		 break;
	}
}

/*******************************************************************************
 * @description    : 화면 로딩시 초기값 세팅 
*******************************************************************************/
function f_init()
{

	div_search.cal_ifDtmFrom.value = sf_getFirstDay();
	div_search.cal_ifDtmTo.value   = Today();

	f_setCombo();
}

/*******************************************************************************
 * @description    : 콤보에 사용될 데이터 바인딩
*******************************************************************************/
function f_setCombo()
{
	http.Sync = true;
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("ds_prodCl" , "ZBAS_C_00050");   //카드사	
	cf_setCommonCode("ds_prodSU" , "ZSAL_C_00030");   //단말기결재조건
	cf_setCommonCode("ds_prodCU" , "ZSAL_C_00220");   //고객구분
	cf_setCommonCode("ds_prodCO" , "ZBAS_C_00040");   //단말기색상
	cf_setCommonCode("ds_prodPRO" , "ZSAL_C_00330", "전체");   //처리구분
	cf_checkCommonCode(); 	
	http.Sync = false;
}

/*******************************************************************************
 * @description    : 권한 설정
*******************************************************************************/
function f_setAuth()
{
	f_getSession();
}

/*******************************************************************************
 * @description    : 글로벌 섹션 정보
*******************************************************************************/
function f_getSession()
{
	FV_USER_ID        = gds_session.GetColumn(0, "loginid");					// 사용자ID
	FV_USER_NM        = gds_session.GetColumn(0, "usernm");						// 사용자명

	FV_ORG_ID         = gds_session.GetColumn(0, "orgid");						// 소속 
	FV_ORG_NM         = gds_session.GetColumn(0, "orgnm");						// 소속명
	FV_ORG_CL         = gds_session.GetColumn(0, "orgcl");						// 소속구분
	FV_AGENCY_ID      = gds_session.GetColumn(0, "posAgencyId");        		// 소속 대리점ID
	FV_AGENCY_NM      = gds_session.GetColumn(0, "posAgencyNm");        		// 소속 대리점명
	FV_AGENCY_UKEY_ID = gds_session.GetColumn(0, "posAgencyUkeyId");    		// 소속 대리점UKEY ID	

	FV_ORG_AREA_ID            = gds_session.GetColumn(0, "orgareaid");			// 근무지 ID
	FV_ORG_AREA_NM            = gds_session.GetColumn(0, "orgareanm");			// 근무지명
	FV_ORG_AREA_POS_AGENCY_CD = gds_session.GetColumn(0, "orgareaposagencycd");	// 근무지 
	FV_ORG_AREA_UKEY_ID       = gds_session.GetColumn(0, "orgareaukeyid");      // 근무지ukeyid
	FV_ORG_AREA_SUB_CD        = gds_session.GetColumn(0, "orgareaukeysubcd");   // 근무지subcd
	FV_ORG_AREA_CHANNEL_CD    = gds_session.GetColumn(0, "orgareaukeychannelcd");  // 근무지 channelcd	
	FV_USER_GRP               = gds_session.GetColumn(0, "usergrp");			// 유저그룹
	FV_ORG_AREA_CL1           = gds_session.GetColumn(0, "orgareacl1");			// 근무지 구분
	FV_DUTY                   = gds_session.GetColumn(0, "duty");	   		    // 직책		

}

/*******************************************************************************
 * @description    :  검색전 날짜 유효성 검사
 *******************************************************************************/
function f_validSearch()
{	
	return sf_isValidDateFromTo(div_search.cal_ifDtmFrom, div_search.cal_ifDtmTo, 0, true);
}

/*******************************************************************************
 * @description    :  그리드 더블 클릭 이벤트 
*******************************************************************************/
function grd_List_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sMsg;

	var gnrlSaleNo = ds_list.GetColumn(ds_list.currow,"GNRL_SALE_NO");
	var gnrlSaleChgSeq = ds_list.GetColumn(ds_list.currow,"GNRL_SALE_CHG_SEQ");

	if(!uf_isEmpty(gnrlSaleNo + "") && !uf_isEmpty(gnrlSaleChgSeq + ""))
	{
		var sArg = "FV_ROW=" + quote(nRow);
		dialog("SAL::SALBOS00200.xml", sArg, "830","600");
	}
	else
	{
		sMsg = "서비스에 해당하는 일반판매 정보가 없습니다.";
		var sArg = "rmks=" + quote( sMsg);
		dialog("SAL::SALSCO01200.xml", sArg, 410, 446);
		return;
	}
}

/*******************************************************************************
 * @description    : 엑셀저장
*******************************************************************************/
function Div0_btn_ExcelExport_OnClick(obj)
{
	cf_exportExcel(grd_list);

	ds_xlslog.Clear();
	cf_exportExcelLog(ds_xlslog, grd_list, "B2C(On Line) > 상품판매관리", pFrmNum, ds_list.GetRowCount());
}

/*******************************************************************************
 * @description    : 화면 재설정
*******************************************************************************/
function df_reSet(sArg)
{
	if(uf_isNull(sArg) == true)
	{

	}
	else
	{
		cf_setArg("ds_ref", sArg);	

		cf_init(div_search);
		ds_list.DeleteAll();

		OP_DT_FR        = ds_ref.GetColumn(0, "OP_DT_FR");
		OP_DT_TO        = ds_ref.GetColumn(0, "OP_DT_TO");
		POS_AGENCY_NM   = ds_ref.GetColumn(0, "POS_AGENCY_NM");
		POS_AGENCY_UKEY = ds_ref.GetColumn(0, "POS_AGENCY_UKEY");
		POS_AGENCY_PS   = ds_ref.GetColumn(0, "POS_AGENCY_PS");  
		SALE_PLC_NM     = ds_ref.GetColumn(0, "SALE_PLC_NM");
		UKEY_AGENCY_CD  = ds_ref.GetColumn(0, "UKEY_AGENCY_CD");
		UKEY_SUB_CD     = ds_ref.GetColumn(0, "UKEY_SUB_CD");
		UKEY_CHANNEL_CD = ds_ref.GetColumn(0, "UKEY_CHANNEL_CD");
		SALE_PLC        = ds_ref.GetColumn(0, "SALE_PLC");

		FV_ORD_MGMT_NO    = ds_ref.GetColumn(0, "sOrdMgmtNo");
		FV_PARENT_PAGE_NO = ds_ref.GetColumn(0, "sParentPageNo");

		f_init();
	}
}

/*******************************************************************************
 * @description    : 상담관리 암호화
*******************************************************************************/
function btn_Crypto_OnClick(obj)
{
	if(!confirm("상담관리 테이블[주민번호/계좌번호/전화번호/서비스번호]을 암호화 하시겠습니까?"))
	{
		return;
	}

	// 트랜잭션 설정
	var sSvcID = "svcCrypto";
	var sInDs  = "nc_input_fieldDs=ds_crypto";
	var sOutDs = "";
	var sArg   = "sktst.sal.BOS#saveCrypto";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);
}

