var FV_SL_CL;            //영업구분

var FV_DIS_FIRST_SEARCH_YN;  //재고-단말기 상태 조회 구분  onload : 01 , 판매처,판매일자 : 02
var FV_GET_SPLST_MDL_YN;     //모델별 판매가격표 조회 여부
var FV_DIS_EQP_ERR_STATUS  = "0";  //단말기오류상태 ( 0:정상, 1:오류)
var FV_DIS_USIM_ERR_STATUS = "0";  //USIM  오류상태 ( 0:정상, 1:오류)

var FV_IF_PROC_CL;           //처리구분  1:UkeyIf , 2:ps
var FV_OP_DT;
var FV_OP_TM;
var FV_SEQ;
var FV_UPD_CNT;

var FV_LOGIN_USER_ID;    //로그인 유저 아이디 
var FV_LOGIN_USER_NM;    //로그인 유저 명
var FV_LOGIN_DUTY;       //권한구분

var FV_CHK_EQP_YN;       //단말기 판매여부
var FV_CHK_USIM_YN;      //USIM 판매여부

var FV_OLD_RTN_YN;

var FV_DIS_EQP_YN;       //재고 단말기 등록 여부
var FV_DIS_USIM_YN;      //재고 USIM 등록 여부

/*******************************************************************************
 * @description    : 최초 폼 로드시 발생 - 이벤트
*******************************************************************************/
function SALRGS00100_OnLoadCompleted(obj)
{
	cf_setAuth(pFrmNum);	// 권한 공통

    //로컬변수 초기화
    lf_formVarInit();

    //콤보박스 초기화
	lf_combomInit();

    //폼 초기화
	lf_formInit();

	//일반판매전문정보 취득
	lf_getGeneralSaleIfInfo();
}

/*******************************************************************************
 * @description         : 로컬 변수 초기화 초기화
*******************************************************************************/
function lf_formVarInit()
{
    //영업관리 U.Key I/F 현황 화면 param 
    FV_IF_PROC_CL    = p_if_proc_cl;  //처리구분  1:UkeyIf , 2:ps
    FV_OP_DT         = p_op_dt;       //전송일자
    FV_OP_TM         = p_op_tm;       //전송시각
    FV_SEQ           = p_seq;         //순번

    //global 변수 
    FV_LOGIN_USER_ID = gds_session.GetColumn(0,"loginId"); //로그인 유저 아이디 
    FV_LOGIN_USER_NM = gds_session.GetColumn(0,"userNm");  //로그인 유저 명
    FV_LOGIN_DUTY    = gds_session.GetColumn(0,"duty");    //권한    

    //local 처리 변수
    FV_DIS_FIRST_SEARCH_YN = "Y";    

    FV_OLD_RTN_YN = "N";
}

/*******************************************************************************
 * @description         : 콤보박스 초기화
*******************************************************************************/
function lf_combomInit()
{
    http.Sync = true;
	// 공통 코드를 위한 데이타셋 설정
	cf_setCommonCodeDsEnv();

	//콤보박스 및 Combo DataSet초기화
	cf_setCommonCode("div_dealCoInfo.cmb_slCl"      , "ZSAL_C_00260");          //영업구분
	cf_setCommonCode("div_custInfo.cmb_custCl"      , "ZSAL_C_00220" );         //고객구분

	cf_setCommonCode("div_eqpInfo.rdo_settlCond"    , "ZSAL_C_00030");		    //단말기정보 - 단말기결제조건
	cf_setCommonCode("div_eqpInfo.cmb_allotPrdCd"   , "ZPOL_C_00230");	        //단말기정보 - 할부기간
	cf_setCommonCode("div_usimInfo.rdo_usimAllotCl" , "ZSAL_C_00020");	        //USIM정보   - 결제조건

	cf_setCommonCode("ds_commSlNetCd"               , "ZPOL_C_00101");          //판매정보 - 영업망
	cf_setCommonCode("ds_commSaleDtlTyp"            , "ZPOL_C_00040");	        //판매정보 - 판매세부유형
	cf_setCommonCode("div_saleInfo.cmb_dsNetCd"     , "ZPOL_C_00110");	        //판매정보 - 유통망
	cf_setCommonCode("div_saleInfo.cmb_agrmtPrdCd"  , "ZPOL_C_00230" , "선택");	//판매정보 - 약정기간코드
	cf_setCommonCode("div_saleInfo.cmb_addSuplCd"   , "ZPOL_C_00060" , "선택");	//판매정보 - 추가판매지원유형
	cf_setCommonCode("div_saleInfo.cmb_ocbMcard"    , "ZSAL_C_00190" , "선택");	//판매정보 - OCB/Mcard

	cf_setCommonCode("div_sktPrprc.cmb_scrbFeeCl"   , "ZSAL_C_00130");          //SKT예수금 - 가입비
	cf_setCommonCode("div_sktPrprc.cmb_mnpCmmsCl"   , "ZSAL_C_00150");          //SKT예수금 - MNP
	cf_setCommonCode("div_sktPrprc.cmb_frgGrtInsuCl", "ZSAL_C_00210");          //SKT예수금 - 외국인보증보험료

	// 설정된 코드를 기반으로 공통 코드를 추가 및 데이타 바인딩
	cf_checkCommonCode();
	http.Sync = false;

}

