            //lf_getDisInfo();
            if ( ds_generalSaleIfInfo.GetColumn(0,"sale_chg_hst_cl") == "02" )
            {
                ds_generalSaleIfInfo.SetColumn(0,"eqp_ser_num",ds_generalSaleIfInfo.GetColumn(0,"bf_eqp_ser_num"));
                ds_generalSaleIfInfo.SetColumn(0,"eqp_mdl_cd",ds_generalSaleIfInfo.GetColumn(0,"bf_eqp_mdl_cd"));

