                ds_generalSaleIfInfo.SetColumn(0,"usim_mdl_cd",ds_generalSaleIfInfo.GetColumn(0,"bf_usim_mdl_cd"));
            }
            else if ( ds_generalSaleIfInfo.GetColumn(0,"sale_chg_hst_cl") == "03" ) //할부추가
            {
                ds_generalSaleIfInfo.SetColumn(0,"eqp_settl_cond","C");
                ds_generalSaleIfInfo.SetColumn(0,"allot_sale_prc","0");
            }
            else if ( ds_generalSaleIfInfo.GetColumn(0,"sale_chg_hst_cl") == "04" ) //할부취소
