            if ( uf_IsNull(trim(ds_generalSaleIfInfo.GetColumn(0,"eqp_ser_num"))) 
                 && uf_IsNull(trim(ds_generalSaleIfInfo.GetColumn(0,"eqp_mdl_cd")))
                 && uf_IsNull(trim(ds_generalSaleIfInfo.GetColumn(0,"color_cd"))) )
