                ds_generalSaleIfInfo.SetColumn(0,"eqp_settl_cond_nm"  , "");
                ds_generalSaleIfInfo.SetColumn(0,"allot_prd_cd"       , "");
                ds_generalSaleIfInfo.SetColumn(0,"allot_prd_nm"       , "");
                ds_generalSaleIfInfo.SetColumn(0,"allot_sale_prc"     , "0");
            }

