        {
		    var sArg  = "param1=" +quote(ds_gnrlSaleNo.GetColumn(0,"gnrl_sale_no"));
		        sArg += " param2="+quote(ds_gnrlSaleNo.GetColumn(0,"gnrl_sale_chg_seq"));

	       // 팝업 함수
	       dialog("SAL::SALRES00200.xml", sArg, 600,830);
           cf_callnClose(excuteMethod, "");	       	         
        }
        else
        {
            //gf_OpenWindow( "100000175", sArg);
            //this.Close();
            cf_callnClose(excuteMethod, "");            
        }
    }
    return;
}

/*******************************************************************************
 * @description    : 단말기 중고판매 여부에 따른 판매세부유형 filter
*******************************************************************************/
function lf_filterCmbSaleDtlTyp(sEqpOldSaleYn)
{
    if (sEqpOldSaleYn == "Y")
    {
        //중고판매일때
        ds_commSaleDtlTyp.Filter("substr(comm_cd_val,0,2)=='S3' || comm_cd_val == ''");
    }
    else
    {
        if ( uf_IsNull(ds_generalSaleIfInfo.GetColumn(0,"sale_typ")) )
        {
            ds_commSaleDtlTyp.Filter("substr(comm_cd_val,0,2)!='S3'");
        }
        else
        {
            ds_commSaleDtlTyp.Filter("substr(comm_cd_val,0,4)="+substr(ds_generalSaleIfInfo.GetColumn(0,"sale_typ"),0,4) );
        }
    }

    div_saleInfo.cmb_saleDtlTyp.Index = 0;
}

/*******************************************************************************
 * @description    : 판매가격 
*******************************************************************************/
function lf_enablePrcInfo(sSettlCond)
{
    if (sSettlCond == "C")
    {
        div_prcInfo.msk_eqpCashSalePrc.Enable = true;
    }
    else
    {
        div_prcInfo.msk_eqpCashSalePrc.Enable = false;
    }
}

/*******************************************************************************
 * @description    : USIM 결제조건에 따른 edit box처리 
*******************************************************************************/
function div_prodInfo_rdo_usimAllotCl_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	if ( strCode == "N" )   
	{   //선납

	    if (  !uf_IsNull(div_usimInfo.edt_usimMdlCd.Text) )
	    {
	        var sSaleDtm = div_general.cal_saleDtm.Value + div_general.edt_saleDtmHh.Text + div_general.edt_saleDtmMm.Text + "00";
            cf_setParam(ds_input, "mdl_id"     , ds_.edt_usimMdlCd.value);       //USIM모델코드
            cf_setParam(ds_input, "sale_dtm"   , sSaleDtm ); //판매일시

	        var sSvcID = "getUplst";
	        var sInDs  = "nc_input_fieldDs=ds_input";
	        var sOutDs = "ds_uplst=List";
	        var sArg   = "sktst.sal.SCO#getUplst";	

	        cf_Transaction(sSvcID, sInDs, sOutDs, sArg);  	
	    }
	    else
	    {
	        alert("USIM 모델을 선택해 주세요.");
	        return;
	    }


	    //div_prodInfo.msk_usimSalePrc.text = "";
	    //div_prodInfo.msk_usimSalePrc.Enable = false;
	    //div_prodInfo.msk_usimSalePrc.SetFocus();
	}
	else
	{   //후납
	    div_prodInfo.msk_usimSalePrc.Value = 0;
	    div_prodInfo.msk_usimSalePrc.Enable = false;
	}	
}

/*******************************************************************************
 * @description    : 단말기 결제조건에 따라 할부콤보 처리 
*******************************************************************************/
function div_saleInfo_rdo_settlCond_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

    //할부 : A, 현금 : C
	if ( strCode == "A" )
	{
	    div_eqpInfo.cmb_allotPrdCd.Enable = true;
	    div_eqpInfo.cmb_allotPrdCd.Index = 0;

	    //현금판매가 수정불가.
	    div_prcInfo.msk_eqpAllotSalePrc.Enable = true;
	    div_prcInfo.msk_eqpCashSalePrc.Value = "0" ;
	    div_prcInfo.msk_eqpCashSalePrc.Enable = false;

	}
	else
	{
	    div_eqpInfo.cmb_allotPrdCd.Enable = false;
	    div_eqpInfo.cmb_allotPrdCd.value = "";

	    //현금판매가 수정가능.
	    div_prcInfo.msk_eqpAllotSalePrc.Value = "0" ;
	    div_prcInfo.msk_eqpAllotSalePrc.Enable = false;
	    div_prcInfo.msk_eqpCashSalePrc.Enable = true;
	}

	/*
	if ( FV_GET_SPLST_MDL_YN == "Y" )
    {
		//모델별 판매단가표 조회
		f_getSplstMdlInfo();
	}
	*/
}

