/*******************************************************************************
 * @description    : 단말기 정보보조회
*******************************************************************************/
function div_eqpInfo_edt_eqpSerNum_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
    if ( nChar == 13)
    {
        if ( div_eqpInfo.chk_eqpOldSaleYn.value == "Y" )
        {
		}
		else
		{
			if (length(obj.text) < 7)
			{
				obj.Text = lpad( obj.text , "0", 7);    
			}

			if (!lf_searchSaleDtlTypChk("EqpMdlCd"))
			{
				return ;
			}

			var sSvcId = "getEqpMdlInfo";                   //callback svcId
			var sSerNum = div_eqpInfo.edt_eqpSerNum.Text;   //일련번호
			var sProdCl = "1";                              //상품구분

			f_getDisProdInfo(sSvcId, sSerNum, sProdCl);

		}
	}     
}

