function lf_getSvcPlcInfo()
{
    sf_clearControls("div_dealCoInfo.edt_svcPlcUkeyCd,div_dealCoInfo.edt_svcPlcSubCd,div_dealCoInfo.edt_svcPlcChannelCd");
    var sSvcDtm = div_dealCoInfo.cal_svcDtm.Value + div_dealCoInfo.edt_svcDtmHh.Text + div_dealCoInfo.edt_svcDtmMm.Text;
	var sArg  = "deal_co_cl1="  + quote("A3");
		sArg += " deal_status=" + quote("3"); 	//옵션 (거래처 상태) - 1: 수납정지 N  , 2: 출고정지 N , 3: 판매정지 N 
		sArg += " pos_agency="  + quote(gds_session.GetColumn(0,"posAgencyId")); // 옵션	    
		sArg += " eff_dtm="     + quote(sSvcDtm);


    if ( !uf_IsNull(div_dealCoInfo.edt_salePlc.Text) )
    {
        sArg += " deal_co_cd=" + quote(div_dealCoInfo.edt_svcPlc.Text);
    }

	cf_callDealPop(sArg, div_dealCoInfo, "edt_svcPlcNm,edt_svcPlc,edt_svcPlcUkeyCd,edt_svcPlcSubCd,edt_svcPlcChannelCd");

	return;
}

/*******************************************************************************
 * @description    : 부가서비스 선택 팝업 화면 종료시 호출
*******************************************************************************/
function lf_supservicePopupClose()
{

	if(ds_suplservice.GetRowCount() < 1)
	{
		ds_suplservice.AddRow();
	}

    ds_suplservice.Copy("ds_param");

	//cf_copyDataSet(dds_supSvcSelect,s_param);
}

/*******************************************************************************
 * @description    : 저장
*******************************************************************************/
function btn_save_OnClick(obj)
{

    if ( !lf_saveValidationCheck() )
    {
        return;
    }

	//일반판매
	//ds_generalSale
    var sSaleDtm = div_dealCoInfo.cal_saleDtm.Value + div_dealCoInfo.edt_saleDtmHh.Text + div_dealCoInfo.edt_saleDtmMm.Text + "00";
    var sSvcDtm  = div_dealCoInfo.cal_svcDtm.Value + div_dealCoInfo.edt_svcDtmHh.Text + div_dealCoInfo.edt_svcDtmMm.Text + "00";
    var sSaleChgHstCl = "01";

    cf_setParam(ds_generalSale, "sale_dtm"           , sSaleDtm );                                           //판매일시
    cf_setParam(ds_generalSale, "sale_chgrg_id"      , div_dealCoInfo.cmb_saleChgrgId.Value );               //판매담당자ID
    cf_setParam(ds_generalSale, "sale_chg_hst_cl"    , sSaleChgHstCl );                                      //판매변경이력구분  - 판매 : 01
    cf_setParam(ds_generalSale, "sale_chg_dtm"       , sSaleDtm );                                           //판매변경일시
    cf_setParam(ds_generalSale, "sale_mod_user_id"   , "" );                                                 //판매변경자ID
    cf_setParam(ds_generalSale, "stl_plc"            , div_dealCoInfo.edt_stlPlc.Text );                     //정산처코드
    cf_setParam(ds_generalSale, "sale_plc"           , div_dealCoInfo.edt_salePlc.Text );                    //판매처코드
    cf_setParam(ds_generalSale, "svc_mgmt_num"       , div_custInfo.edt_svcMgmtNum.Text );                   //서비스관리번호
    cf_setParam(ds_generalSale, "inden_num"          , div_saleInfo.edt_saleAprvNum.Text );                  //계약서번호
    cf_setParam(ds_generalSale, "cust_cl"            , div_custInfo.cmb_custCl.Value );                      //고객구분
    cf_setParam(ds_generalSale, "cust_nm"            , div_custInfo.edt_custNm.Text );                       //고객명
    cf_setParam(ds_generalSale, "cust_biz_num"       , div_custInfo.edt_custBizNum.Text );                   //주민법인번호
    cf_setParam(ds_generalSale, "cust_birth_year"    , substr(div_custInfo.edt_custBizNum.Text,0,2) );       //고객출생년도
    cf_setParam(ds_generalSale, "svc_plc"            , div_dealCoInfo.edt_svcPlc.Text );                     //개통처
    cf_setParam(ds_generalSale, "svc_num"            , div_custInfo.edt_svcNum.Text );                       //개통번호
    cf_setParam(ds_generalSale, "svc_dtm"            , sSvcDtm );                                            //개통일시
    cf_setParam(ds_generalSale, "svc_chgrg_id"       , ds_generalSaleIfInfo.GetColumn(0,"proc_chgrg_id") );  //개통담당자ID
    cf_setParam(ds_generalSale, "sl_cl"              , FV_SL_CL);                                //영업구분
    cf_setParam(ds_generalSale, "sl_net_cd"          , div_saleInfo.cmb_slNetCd.Value );         //영업망코드
    cf_setParam(ds_generalSale, "ds_net_cd"          , div_saleInfo.cmb_dsNetCd.Value );         //유통망코드
    cf_setParam(ds_generalSale, "sale_dtl_typ"       , div_saleInfo.cmb_saleDtlTyp.Value );      //판매세부유형
    cf_setParam(ds_generalSale, "settl_cond"         , div_eqpInfo.rdo_settlCond.Value );        //단말기결제조건
    cf_setParam(ds_generalSale, "usim_allot_cl"      , div_usimInfo.rdo_usimAllotCl.Value );     //USIM결제조건
    cf_setParam(ds_generalSale, "allot_prd_cd"       , div_eqpInfo.cmb_allotPrdCd.Value );       //단말기할부기간코드
    cf_setParam(ds_generalSale, "agrmt_prd_cd"       , div_saleInfo.cmb_agrmtPrdCd.Value );      //T약정기간코드
    cf_setParam(ds_generalSale, "add_suprt_cd"       , div_saleInfo.cmb_addSuptCd.Value );       //추가판매지원유형
    cf_setParam(ds_generalSale, "online_plc"         , div_dealCoInfo.edt_onlinePlc.Text );      //온라인 판매처
    cf_setParam(ds_generalSale, "sale_cmms_fix_yn"   , "" );                                     //판매수수료확정여부
    cf_setParam(ds_generalSale, "online_cmms_fix_yn" , "" );                                     //온라인판매수수료확정여부
    cf_setParam(ds_generalSale, "dis_hld_plc"        , div_dealCoInfo.edt_disHldPlc.Text );      //재고보유처
    cf_setParam(ds_generalSale, "rmks"               , "DUMMY 생성 - "+div_rmks.txa_rmks.Text ); //비고
    cf_setParam(ds_generalSale, "ins_user_id"        , FV_LOGIN_USER_ID );                       //입력자ID   
    cf_setParam(ds_generalSale, "sale_cncl_yn"       , "N");                                     //판매취소여부
    cf_setParam(ds_generalSale, "acc_chg_hst_cl"     , sf_getAccChgHstCl(sSaleChgHstCl) );       //정산변경이력구분  


    //cf_setParam(ds_generalSale, "eqp_allot_bond_corp"  , "01" );  //단말기할부채권사 - SKT : 01 
    //cf_setParam(ds_generalSale, "usim_allot_bond_corp" , "01" );  //USIM할부채권사- SKT : 01

    if (div_eqpInfo.rdo_settlCond.Value == "A")
    {   //할부 
        cf_setParam(ds_generalSale, "eqp_allot_bond_corp"  , "01" );  //단말기 할부채권사 : ZSAL_C_00200
    }
    else
    {   //현금
        cf_setParam(ds_generalSale, "eqp_allot_bond_corp"  , "" );  //단말기 할부채권사 : ZSAL_C_00200
    }

    if (div_usimInfo.rdo_usimAllotCl.Value == "Y")
    {   //후납
        cf_setParam(ds_generalSale, "usim_allot_bond_corp" , "01" );  //USIM 할부채권사 : ZSAL_C_00200
    }
    else
    {   //선납
        cf_setParam(ds_generalSale, "usim_allot_bond_corp" , "" );  //USIM 할부채권사 : ZSAL_C_00200
    }

	//단말기판매
	//ds_equipmentSale
    cf_setParam(ds_equipmentSale, "eqp_ser_num"      , div_eqpInfo.edt_eqpSerNum.Text );    //단말기일련번호
    cf_setParam(ds_equipmentSale, "eqp_mdl_cd"       , div_eqpInfo.edt_eqpMdlCd.Text );     //단말기모델코드
    cf_setParam(ds_equipmentSale, "eqp_color_cd"     , div_eqpInfo.cmb_eqpColorCd.Value );  //단말기Color
    cf_setParam(ds_equipmentSale, "eqp_old_sale_yn"  , div_eqpInfo.chk_eqpOldSaleYn.Value );//단말기중고판매여부
    cf_setParam(ds_equipmentSale, "ins_user_id"      , FV_LOGIN_USER_ID );                  //입력자ID    

	//Usim 판매
	//ds_usimSale
    cf_setParam(ds_usimSale, "usim_ser_num"        , div_usimInfo.edt_usimSerNum.Text );     //USIM일련번호
    cf_setParam(ds_usimSale, "usim_mdl_cd"         , div_usimInfo.edt_usimMdlCd.Text );      //USIM모델코드
    cf_setParam(ds_usimSale, "usim_old_sale_yn"    , div_usimInfo.chk_usimOldSaleYn.Value ); //USIM중고판매여부
    cf_setParam(ds_usimSale, "ins_user_id"         , FV_LOGIN_USER_ID );                     //입력자ID    

	//부가상품판매
	//ds_suplservice
   // if (ds_suplservice.rowcount <= 0)
   // {
   //     ds_suplservice.AddRow();
   // }

	//중고반납기기
	//ds_oldrtnEquipment
    if (ds_oldrtnEquipment.rowcount <= 0)
    {
        ds_oldrtnEquipment.AddRow();
    }	
    cf_setParam(ds_oldrtnEquipment, "ins_user_id"      , FV_LOGIN_USER_ID );  //입력자ID   

    if ( !uf_IsNull(ds_oldrtnEquipment.GetColumn(0,"old_rtn_mdl")) &&
         !uf_IsNull(ds_oldrtnEquipment.GetColumn(0,"old_rtn_ser_num")) )
    {
         FV_OLD_RTN_YN = "Y";
    }


	//수납정보
	//ds_oldrtnEquipment
    if (ds_payment.rowcount <= 0)
    {
        ds_payment.AddRow();
    }	
    cf_setParam(ds_payment, "ins_user_id"        , FV_LOGIN_USER_ID );                        //입력자ID 

	//판매수수료
    cf_setParam(ds_saleCmms, "sale_cmms"          , div_saleCmms.msk_saleCmms.Value );         //판매수수료
    cf_setParam(ds_saleCmms, "supl_svc_sale_cmms" , div_saleCmms.msk_suplSvcSaleCmms.Value );  //부가서비스수수료  
    cf_setParam(ds_saleCmms, "add_sale_cmms"      , div_saleCmms.msk_addSaleCmms.Value );      //추가판매수수료
    cf_setParam(ds_saleCmms, "ins_user_id"        , FV_LOGIN_USER_ID );                        //입력자ID 

    //장려금
    cf_setParam(ds_promotionMoney, "gnrl_pr_mny"        , div_promotionMoney.msk_gnrlPrMny.Value );       //일반장려금
    cf_setParam(ds_promotionMoney, "supl_svc_pr_mny"    , div_promotionMoney.msk_suplSvcPrMny.Value );    //부가서비스장려금
    cf_setParam(ds_promotionMoney, "sale_suprt_amt"     , div_promotionMoney.msk_saleSuprtAmt.Value );    //판매지원금
    cf_setParam(ds_promotionMoney, "add_sale_suprt_amt" , div_promotionMoney.msk_addSaleSuprtAmt.Value ); //추가판매지원금
    cf_setParam(ds_promotionMoney, "ins_user_id"        , FV_LOGIN_USER_ID );  //입력자ID    

    var sUsimAllotSalePrc = "0";
    var sUsimCashSalePrc = "0";
    if ( div_usimInfo.rdo_usimAllotCl.Value == "Y" )
    {
        sUsimAllotSalePrc = div_usimInfo.msk_usimSalePrc.Value;
    }
    else
    {
        sUsimCashSalePrc = div_usimInfo.msk_usimSalePrc.Value;
    }
	//할부매출
	//ds_allotSale 
	cf_setParam(ds_allotSale, "eqp_allot_sale_prc"   , div_prcInfo.msk_eqpAllotSalePrc.Value );  //단말기할부판매가
	cf_setParam(ds_allotSale, "usim_allot_sale_prc"  , sUsimAllotSalePrc );  //usim할부판매가
    cf_setParam(ds_allotSale, "ins_user_id"          , FV_LOGIN_USER_ID );  //입력자ID    

	//SKT예수금
	//ds_sktPrprc
	cf_setParam(ds_sktPrprc, "scrb_fee_cl"      , div_sktPrprc.cmb_scrbFeeCl.Value );     //가입비수납구분
	cf_setParam(ds_sktPrprc, "scrb_fee"         , div_sktPrprc.msk_scrbFee.Value );       //가입비
	cf_setParam(ds_sktPrprc, "mnp_cmms_cl"      , div_sktPrprc.cmb_mnpCmmsCl.Value );     //MNP수수료수납구분
	cf_setParam(ds_sktPrprc, "mnp_cmms"         , div_sktPrprc.msk_mnpCmms.Value );       //MNP수수료
	cf_setParam(ds_sktPrprc, "frg_grt_insu_cl"  , div_sktPrprc.cmb_frgGrtInsuCl.Value );  //외국인보증보험료구분
	cf_setParam(ds_sktPrprc, "frg_grt_insu_fee" , div_sktPrprc.msk_frgGrtInsuFee.Value ); //외국인보증보험료
    cf_setParam(ds_sktPrprc, "ins_user_id"      , FV_LOGIN_USER_ID );  //입력자ID    

	//약정보조금
	//ds_agreementAstamt
	cf_setParam(ds_agreementAstamt, "agrmt_astamt"     , div_saleInfo.msk_agrmtPrdCdAmt.Value );  //약정보조금
	cf_setParam(ds_agreementAstamt, "add_agrmt_astamt" , "" );  //추가약정보조금
    cf_setParam(ds_agreementAstamt, "ins_user_id"      , FV_LOGIN_USER_ID );  //입력자ID    

	//현금매출
	//ds_cashSale
	cf_setParam(ds_cashSale, "eqp_cash_sale_prc" , div_prcInfo.msk_eqpCashSalePrc.Value );  //단말기현금판매가
	cf_setParam(ds_cashSale, "usim_cash_sale_prc", sUsimCashSalePrc );    //USIM현금판매가
	cf_setParam(ds_cashSale, "etc_prod_sale_prc" , "0" );                 //기타상품판매가
    cf_setParam(ds_cashSale, "ins_user_id"       , FV_LOGIN_USER_ID );    //입력자ID    

	//OCB/MCARD
	cf_setParam(ds_ocbMcard, "ocb_mcard_cd"      , div_saleInfo.cmb_ocbMcard.Value );    //ocb/mcard 코드
	cf_setParam(ds_ocbMcard, "ocb_mcard_prc"     , div_saleInfo.msk_ocbMcardPrc.Value ); //ocb/macrd 금액
    cf_setParam(ds_ocbMcard, "ins_user_id"       , FV_LOGIN_USER_ID );  //입력자ID    

	//온라인수납정보
    if (ds_onlinePayment.rowcount <= 0)
    {
        ds_onlinePayment.AddRow();
    }	
    cf_setParam(ds_onlinePayment, "ins_user_id"    , FV_LOGIN_USER_ID );  //입력자ID    

	//온라인수수료
    if (ds_onlineCmms.rowcount <= 0)
    {
        ds_onlineCmms.AddRow();
    }	
    cf_setParam(ds_onlineCmms, "ins_user_id"       , FV_LOGIN_USER_ID );  //입력자ID    

	//수작업재고정보
	var iRowCnt = 0;

	if (FV_CHK_EQP_YN == "Y")
	{
	    if (ds_dummyDisInfo.rowcount <= 0)
	    {
	        ds_dummyDisInfo.AddRow();
	    }
        ds_dummyDisInfo.CopyRow(iRowCnt,"ds_dummyEqpInfo", 0);
        ds_dummyDisInfo.SetColumn(iRowCnt, "ins_user_id", FV_LOGIN_USER_ID);
        iRowCnt ++;
	}

	if (FV_CHK_USIM_YN == "Y")
	{
	    ds_dummyDisInfo.AddRow();
        ds_dummyDisInfo.CopyRow(iRowCnt,"ds_dummyUsimInfo", 0);
        ds_dummyDisInfo.SetColumn(iRowCnt, "ins_user_id", FV_LOGIN_USER_ID);
	}

/*
	//수납정보
	//ds_payment
	cf_setParam(ds_payment, "cash_pay_amt"           , div_payment.msk_cashPayAmt.Value );       //현금수납금액
	cf_setParam(ds_payment, "crdtcrd_corp1"           , div_payment.cmb_crdtcrd_corp1.Value );     //신용카드사1
	cf_setParam(ds_payment, "crdtcrd_pay_amt1"       , div_payment.msk_crdtcrdPayAmt1.Value );   //신용카드수납금액1
	cf_setParam(ds_payment, "crdtcrd_corp2"           , div_payment.cmb_crdtcrd_corp2.Value );     //신용카드사2
	cf_setParam(ds_payment, "crdtcrd_pay_amt2"       , div_payment.msk_crdtcrdPayAmt2.Value );   //신용카드수납금액2
	cf_setParam(ds_payment, "skt_prprc_cash_pay_amt" , div_sktPrprc.msk_sktPrprcCashPayAmt.Value  ); //SKT예수금현금수납금액
	cf_setParam(ds_payment, "skt_prprc_prxpay_amt"   , div_sktPrprc.msk_sktPrprcPrxpayAmt.Value  );  //SKT예수금대압금액
	 */
    var sSlNetCl = "";    //판매번호 채번을 위한 영업망 코드
    if ( FV_SL_CL == "01" ) 
    {
        sSlNetCl = "R";   //소매
    }
    else if (FV_SL_CL ==  "02" )
    {
        sSlNetCl = "A";   //도매
    }
    else if (FV_SL_CL ==  "03" )
    {
       sSlNetCl = "O";
    }
    else if (FV_SL_CL ==  "04" )
    {
       sSlNetCl = "S";
    }

    // 판매번호 채번을 위한 정보
	// 유통망 - R: 소매, A:도매, O:온라인소매, S:온라인 도매
	// 판매구분 - E 공기기판매, G: 일반판매, P:수납대행,S:부가서비스유치
    //ds_input
    ds_input.Clear();
	cf_setParam(ds_input, "sl_net_cl"  , sSlNetCl );           //영업망
	cf_setParam(ds_input, "sale_cl"    , "G" );                //판매구분
	cf_setParam(ds_input, "prod_cl"    , "G" );                //
	cf_setParam(ds_input, "ins_user_id", FV_LOGIN_USER_ID );   //처리자

	cf_setParam(ds_input, "if_proc_cl"  , "3" );   //Ukey Interface 처리구분  1:Ukey if     2:PS   3 : DUMMY
	cf_setParam(ds_input, "op_dt"  , ds_generalSaleIfInfo.GetColumn(0,"op_dt") );   //처리일자
	cf_setParam(ds_input, "op_tm"  , ds_generalSaleIfInfo.GetColumn(0,"op_tm") );   //처리시각
	cf_setParam(ds_input, "seq"    , ds_generalSaleIfInfo.GetColumn(0,"seq") );     //순번
	cf_setParam(ds_input, "upd_cnt", ds_generalSaleIfInfo.GetColumn(0,"upd_cnt") ); //update count

	cf_setParam(ds_input, "old_rtn_yn" , FV_OLD_RTN_YN ); //중고반납여부

    //재고 프로시져
//    var sActFlag   = "SO";         //업무처리구분-판매출고:SO, 기기교환출고:EO      
//    var sProcFlag  = "I";         //Transaction처리 : I  
    var sOutSchdDt = div_dealCoInfo.cal_saleDtm.value;   //출고예정일자
//    var sOutCl     = "204";

    //업무처리구분     -  판매취소입고 : SI  , 기기교환입고 : EI
    //업무처리구분코드 -  판매취소입고 : 105 , 기기교환입고 : 104 
//    var sEqpInActFlag  = "";
//    var sEqpInCl       = "";
//    var sUsimInActFlag = "";
//    var sUsimInCl      = "";

    //업무처리구분     -  판매출고 : SO  , 기기교환출고 : EO
    //업무처리구분코드 -  판매출고 : 204 , 기기교환출고 : 205 
    var sEqpActFlag    = "SO";                                         //업무처리구분
    var sEqpProcFlag   = "I";                                          //트랜잭션
    var sEqpOutCl      = "204";                                        //업무처리구분코드
    var sEqpOutSchdDt  = sOutSchdDt;                                   //출고일자 
    var sEqpOutPlcId   = ds_disEqpInfo.GetColumn(0,"dis_hld_plc_id");  //출고처
    var sEqpProdCl     = "1";                                          //상품구분 1:단말기, 2:USIM
    var sEqpProdCd     = ds_disEqpInfo.GetColumn(0,"eqp_mdl_cd");      //상품코드(모델코드)
    var sEqpSerNum     = ds_disEqpInfo.GetColumn(0,"eqp_ser_num");     //일련번호
    var sEqpColorCd    = ds_disEqpInfo.GetColumn(0,"color_cd");        //색상코드
    var sEqpBadYn      = "01";                                         //불량여부
    var sEqpDisSt      = "01";                                         //재고상태
    var sEqpOutQty     = "1";                                          //출고수량
    var sEqpUpdCnt     = ds_disEqpInfo.GetColumn(0,"dis_upd_cnt");                                           //update count
    var sEqpOldSaleYn  = div_eqpInfo.chk_eqpOldSaleYn.Value;

    //cf_setParam(ds_disOutEqpInfo, "old_sale_yn"       , div_eqpInfo.chk_eqpOldSaleYn.Value ); //중고판매여부
    cf_setParam(ds_disOutEqpInfo, "act_flag"          , sEqpActFlag);      //업무처리구분-판매출고:SO, 기기교환출고:EO      
    cf_setParam(ds_disOutEqpInfo, "proc_flag"         , sEqpProcFlag);     //Transaction처리 : I       
    //cf_setParam(ds_disEqpInfo, "out_mgmt_no"       , "");                //출고관리번호 (채번 procedure)    
    cf_setParam(ds_disOutEqpInfo, "out_cl"            , sEqpOutCl);        //출고구분- 판매출고:204, 기기교환출고:205 
    cf_setParam(ds_disOutEqpInfo, "out_schd_dt"       , sEqpOutSchdDt);    //출고예정일자
    cf_setParam(ds_disOutEqpInfo, "out_plc_id"        , sEqpOutPlcId);     //출고처ID
    cf_setParam(ds_disOutEqpInfo, "prod_cl"           , sEqpProdCl);       //상품구분 - 단말기, USIM  
    cf_setParam(ds_disOutEqpInfo, "prod_cd"           , sEqpProdCd);       //상품코드   
    cf_setParam(ds_disOutEqpInfo, "color_cd"          , sEqpColorCd);      //색상코드  
    cf_setParam(ds_disOutEqpInfo, "ser_num"           , sEqpSerNum);       //일련번호   
    cf_setParam(ds_disOutEqpInfo, "bad_yn"            , sEqpBadYn);        //불량여부    
    cf_setParam(ds_disOutEqpInfo, "dis_st"            , sEqpDisSt);        //재고상태    
    cf_setParam(ds_disOutEqpInfo, "out_qty"           , sEqpOutQty);       //출고수량   
    cf_setParam(ds_disOutEqpInfo, "user_id"           , FV_LOGIN_USER_ID); //처리자   	
    cf_setParam(ds_disOutEqpInfo, "rmks"              , "단말기판매출고"); //비고-판매출고, 기기교환출고      
    cf_setParam(ds_disOutEqpInfo, "eqp_old_sale_yn"   , sEqpOldSaleYn);    //중고판매여부   

    var sUsimActFlag    = "SO";                                            //업무처리구분
    var sUsimProcFlag   = "I";                                             //트랜잭션
    var sUsimOutCl      = "204";                                           //업무처리구분코드
    var sUsimOutSchdDt  = sOutSchdDt;                                      //출고일자 
    var sUsimOutPlcId   = ds_disUsimInfo.GetColumn(0,"dis_hld_plc_id");    //출고처
    var sUsimProdCl     = "1";                                             //상품구분 1:단말기, 2:USIM
    var sUsimProdCd     = ds_disUsimInfo.GetColumn(0,"usim_mdl_cd");       //상품코드(모델코드)
    var sUsimSerNum     = ds_disUsimInfo.GetColumn(0,"usim_ser_num");      //일련번호
    var sUsimColorCd    = "99";                                            //색상코드
    var sUsimBadYn      = "01";                                            //불량여부
    var sUsimDisSt      = "01";                                            //재고상태
    var sUsimOutQty     = "1";                                             //출고수량
    var sUsimUpdCnt     = ds_disUsimInfo.GetColumn(0,"dis_upd_cnt");       //update count
    var sUsimOldSaleYn  = div_usimInfo.chk_UsimOldSaleYn.Value ;           //중고판매여부

    //cf_setParam(ds_disOutUsimInfo, "old_sale_yn"       , div_usimInfo.chk_usimOldSaleYn.Value ); //중고판매여부
    cf_setParam(ds_disOutUsimInfo, "act_flag"          , sUsimActFlag);    //업무처리구분-판매출고:SO, 기기교환출고:EO      
    cf_setParam(ds_disOutUsimInfo, "proc_flag"         , sUsimProcFlag);   //Transaction처리 : I       
    //cf_setParam(ds_disUsimInfo, "out_mgmt_no"       , "");               //출고관리번호 (채번 procedure)    
    cf_setParam(ds_disOutUsimInfo, "out_cl"            , sUsimOutCl);      //출고구분- 판매출고:204, 기기교환출고:205 
    cf_setParam(ds_disOutUsimInfo, "out_schd_dt"       , sUsimOutSchdDt);  //출고예정일자
    cf_setParam(ds_disOutUsimInfo, "out_plc_id"        , sUsimOutPlcId);   //출고처ID
    cf_setParam(ds_disOutUsimInfo, "prod_cl"           , sUsimProdCl);     //상품구분 - 단말기, USIM  
    cf_setParam(ds_disOutUsimInfo, "prod_cd"           , sUsimProdCd);     //상품코드   
    cf_setParam(ds_disOutUsimInfo, "color_cd"          , sUsimColorCd);    //색상코드  
    cf_setParam(ds_disOutUsimInfo, "ser_num"           , sUsimSerNum);     //일련번호   
    cf_setParam(ds_disOutUsimInfo, "bad_yn"            , sUsimBadYn);      //불량여부    
    cf_setParam(ds_disOutUsimInfo, "dis_st"            , sUsimDisSt);      //재고상태    
    cf_setParam(ds_disOutUsimInfo, "out_qty"           , sUsimOutQty);     //출고수량   
    cf_setParam(ds_disOutUsimInfo, "user_id"           , FV_LOGIN_USER_ID); //처리자   	
    cf_setParam(ds_disOutUsimInfo, "rmks"              , "USIM판매출고");  //비고-판매출고, 기기교환출고      
    cf_setParam(ds_disOutUsimInfo, "usim_old_sale_yn"  , sUsimOldSaleYn);  //중고판매여부           




/*    
    cf_setParam(ds_procInput, "iv_act_flag"          , "SO"); //업무처리구분-판매출고:SO, 기기교환출고:EO      
    cf_setParam(ds_procInput, "iv_proc_flag"         , "I"); //Transaction처리 : I       
    cf_setParam(ds_procInput, "iv_out_mgmt_no"       , ""); //출고관리번호 (채번 procedure)    
    cf_setParam(ds_procInput, "iv_gnrl_sale_no"      , ""); //일반판매번호
    cf_setParam(ds_procInput, "iv_gnrl_sale_chg_seq" , ""); //일반판매변경순번 
    cf_setParam(ds_procInput, "iv_out_schd_dt"       , div_dealCoInfo.cal_saleDtm.value); //출고예정일자
    cf_setParam(ds_procInput, "iv_out_plc_id"        , ""); //출고처ID
    cf_setParam(ds_procInput, "iv_out_cl"            , "204"); //출고구분- 판매출고:204, 기기교환출고:205 
    cf_setParam(ds_procInput, "iv_rmks"              , "판매출고"); //비고-판매출고, 기기교환출고      
    cf_setParam(ds_procInput, "iv_prod_cl"           , ""); //상품구분 - 단말기, USIM  
    cf_setParam(ds_procInput, "iv_prod_cd"           , ""); //상품코드   
    cf_setParam(ds_procInput, "iv_color_cd"          , ""); //색상코드  
    cf_setParam(ds_procInput, "iv_bad_yn"            , "N"); //불량여부    
    cf_setParam(ds_procInput, "iv_dis_st"            , ""); //재고상태    
    cf_setParam(ds_procInput, "iv_ser_num"           , ""); //일련번호   
    cf_setParam(ds_procInput, "iv_out_qty"           , "1"); //출고수량   
    cf_setParam(ds_procInput, "iv_user_id"           , ""); //처리자   	
 */

    var sSvcID = "saveSaleInfo";
	var sInDs  = "nc_input_fieldDs=ds_input ds_generalSale=ds_generalSale ds_equipmentSale=ds_equipmentSale";
	    sInDs += " ds_usimSale=ds_usimSale ds_suplservice=ds_suplservice ds_oldrtnEquipment=ds_oldrtnEquipment";
	    sInDs += " ds_allotSale=ds_allotSale ds_sktPrprc=ds_sktPrprc ds_agreementAstamt=ds_agreementAstamt";
	    sInDs += " ds_cashSale=ds_cashSale ds_procInput=ds_procInput ds_ocbMcard=ds_ocbMcard";
	    sInDs += " ds_payment=ds_payment ds_onlinePayment=ds_onlinePayment ds_onlineCmms=ds_onlineCmms";
	    sInDs += " ds_saleCmms=ds_saleCmms ds_promotionMoney=ds_promotionMoney";
	    sInDs += " ds_disEqpInfo=ds_disEqpInfo ds_disUsimInfo=ds_disUsimInfo";
	    sInDs += " ds_disOutEqpInfo=ds_disOutEqpInfo ds_disOutUsimInfo=ds_disOutUsimInfo";
	    sInDs += " ds_dummyDisInfo=ds_dummyDisInfo";

	var sOutDs = "ds_gnrlSaleNo=List";
	var sArg   = "sktst.sal.SCO#saveSaleInfo";	

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
	return;	
}

/*******************************************************************************
 * @description    : 일반판매 저장시 상품구분-단말기,USIM validation check
*******************************************************************************/
function lf_saveValidationCheck()
{        
    var bFlag = true;

        bFlag = lf_eqpValidationCheck();

        if (bFlag == true)
        {
            bFlag = lf_usimValidatinCheck();
        }
/*            
    if ( div_dealCoInfo.chk_prodTypEqp.Value == "Y" && div_dealCoInfo.chk_prodTypUsim.Value == "Y") // 단말기, Usim
    {
        bFlag = lf_eqpValidationCheck();

        if (bFlag == true)
        {
            bFlag = lf_usimValidatinCheck();
        }
    }
    else if ( div_dealCoInfo.chk_prodTypEqp.Value == "Y" )
    {
        bFlag = lf_eqpValidationCheck();
    }
    else if ( div_dealCoInfo.chk_prodTypUsim.Value == "Y" )
    {
        bFlag = lf_usimValidatinCheck();
    }
    else
    {
        alert("상품구분을 선택해 주십시요.");
        bFlag = false;
    }
*/  
	// 마감월 체크
	cf_CheckCloseDay("02", div_dealCoInfo.cal_saleDtm.Value, "판매일자"); // 03 정산
	//cf_CheckCloseMnth("03", div_dealCoInfo.cal_saleDtm.Value, "판매일자"); // 03 정산
	if(FV_BOOLEAN_CLOSE) return false; // 입력한 일자가 마감 되었을 경우 '1'(true) 리턴.  

    if ( uf_IsNull(div_dealCoInfo.cal_saleDtm.Value) )
    {
        alert("판매일자를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_dealCoInfo.edt_saleDtmHh.Text ) )
    {
        alert("판매시간를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_dealCoInfo.edt_saleDtmMm.Text ) )
    {
        alert("판매시간 및 분을 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_dealCoInfo.edt_salePlc.Text ) )
    {
        alert("판매처를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_dealCoInfo.cal_svcDtm.Value) )
    {
        alert("개통일자를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_dealCoInfo.edt_svcDtmHh.Text ) )
    {
        alert("개통시간를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_dealCoInfo.edt_svcDtmMm.Text ) )
    {
        alert("개통시간 및 분을 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_dealCoInfo.edt_svcPlc.Text ) )
    {
        alert("개통처를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_dealCoInfo.edt_svcChgrgNm.Value ) )
    {
        alert("개통담당자를 선택해 주십시요.");
        return false;
    }    

    if ( uf_IsNull(div_dealCoInfo.edt_insUserId.Value ) )
    {
        alert("처리자를 선택해 주십시요.");
        return false;
    }    

    //고객정보 check
    if ( uf_IsNull(div_custInfo.edt_custNm.Text) )
    {
        alert("고객명을 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_custInfo.cmb_custCl.Value ) )
    {
        alert("고객구분을 선택해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_custInfo.edt_svcNum.Text ) )
    {
        alert("개통번호를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_custInfo.edt_custBizNum.Text ) )
    {
        alert("주민/법인번호를 입력해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_custInfo.edt_svcMgmtNum.Text ) )
    {
        alert("서비스관리번호를 입력해 주십시요.");
        return false;
    }

    if (FV_SL_CL == "01" || FV_SL_CL == "03")
    {
        //판매담당
        if (uf_IsNull(div_dealCoInfo.cmb_saleChgrgId.Value))
        {
            alert("판매담당을 선택해 주십시요.");
            return false;
        }
    }
    else if (FV_SL_CL == "02")
    {

    }
    else if (FV_SL_CL == "03")
    {
        //판매담당
        if (uf_IsNull(div_dealCoInfo.cmb_saleChgrgId.Value))
        {
            alert("판매담당을 선택해 주십시요.");
            return false;
        }

        //온라인거래처
        if (uf_IsNull(div_dealCoInfo.edt_onlinePlc.Text) )
        {
            alert("온라인거래처를 입력해 주십시요");
            return false;
        }
    }
    else if (FV_SL_CL == "04")
    {
        //온라인거래처
        if (uf_IsNull(div_dealCoInfo.edt_onlinePlc.Text) )
        {
            alert("온라인거래처를 입력해 주십시요");
            return false;
        }

    }


    var sEqpSaleFlag = "N";
    var sUsimSaleFlag = "N";
    var sRtnMsg = "";
    //단말기 정보 check

    if ( FV_CHK_EQP_YN == "Y" )
    {
        //sRtnMsg = lf_prodCheck("div_eqpInfo.edt_eqpSerNum", "div_eqpInfo.edt_eqpMdlCd", "div_eqpInfo.edt_eqpMdlNm", "div_eqpInfo.rdo_settlCond" , "단말기");
        sEqpSaleFlag = "Y";

        if( uf_IsNull(div_eqpInfo.rdo_settlCond.Value) )
        {
            alert("단말기 결제조건을 선택해주십시요.");
            return false;
        }

        if ( uf_IsNull(div_eqpInfo.cmb_eqpColorCd.Value ) )
        {
            alert("단말기 색상을 선택해주십시요.");
            return false;
        }

        if( uf_IsNull(ds_dummyEqpInfo.GetColumn(0,"in_fix_dt"))
            || uf_IsNull(ds_dummyEqpInfo.GetColumn(0,"in_plc"))
            || uf_IsNull(ds_dummyEqpInfo.GetColumn(0,"prchs_plc"))
            || uf_IsNull(ds_dummyEqpInfo.GetColumn(0,"out_unit_prc")) )
        {
            alert("재고정보(단말기 정보)를 입력해 주십시요.");
            return false;
        }

        if ( ds_dummyEqpInfo.GetColumn(0,"in_fix_dt") > div_dealCoInfo.cal_saleDtm.Value )
        {
            alert("단말기 입고일자를 판매일자 이전 일을 입력해 주십시요.");
            return false;
        }

    }

    //Usim 정보 check
    if ( FV_CHK_USIM_YN == "Y" )
    {
        //sRtnMsg = lf_prodCheck("div_usimInfo.edt_usimSerNum", "div_usimInfo.edt_usimMdlCd", "div_usimInfo.edt_usimMdlNm", "div_usimInfo.rdo_usimAllotCl" , "USIM");
        sUsimSaleFlag = "Y";

        if( uf_IsNull(div_usimInfo.rdo_usimAllotCl.Value) )
        {
            alert("USIM 결제조건을 선택해주십시요.");
            return false;
        }        

        if( uf_IsNull(ds_dummyUsimInfo.GetColumn(0,"in_fix_dt"))
            || uf_IsNull(ds_dummyUsimInfo.GetColumn(0,"in_plc"))
            || uf_IsNull(ds_dummyUsimInfo.GetColumn(0,"prchs_plc"))
            || uf_IsNull(ds_dummyUsimInfo.GetColumn(0,"out_unit_prc")) )
        {
            alert("재고정보(USIM 정보)를 입력해 주십시요.");
            return false;
        }

        if ( ds_dummyUsimInfo.GetColumn(0,"in_fix_dt") > div_dealCoInfo.cal_saleDtm.Value )
        {
            alert("USIM 입고일자를 판매일자 이전 일을 입력해 주십시요.");
            return false;
        }           
    }

    //단말기/Usim 정보 둘다 없을때
    if (sEqpSaleFlag == "N" && (sUsimSaleFlag == "N" && div_usimInfo.chk_UsimOldSaleYn.Value == "N" ) )
    {
        alert("단말기/USIM 판매가 없으면 저장 할 수 없습니다.");
        return false;
    }

    //판매정보 check
    if ( uf_IsNull(div_saleInfo.cmb_slNetCd.Value) )
    {
        alert("영업망을 선택해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_saleInfo.cmb_saleDtlTyp.Value) )
    {
        alert("판매유형을 선택해 주십시요.");
        return false;
    }

    if ( uf_IsNull(div_saleInfo.cmb_dsNetCd.Value) )
    {
        alert("유통망을 선택해 주십시요.");
        return false;
    }

    if ( div_dealCoInfo.cmb_slCl.Value == "01" || div_dealCoInfo.cmb_slCl.Value == "03")
    {
		//판매수납대상금액  validation check
		var iTotSaleAmt     = ParseInt( div_prcInfo.msk_eqpCashSalePrc.Value );
		var iCashPayAmt     = ParseInt( ds_payment.GetColumn(0,"cash_pay_amt") );
		var iCrdtcrdPayAmt1 = ParseInt( ds_payment.GetColumn(0,"crdtcrd_pay_amt1") );
		var iCrdtcrdPayAmt2 = ParseInt( ds_payment.GetColumn(0,"crdtcrd_pay_amt2") );

		if ( iTotSaleAmt != ( iCashPayAmt + iCrdtcrdPayAmt1 + iCrdtcrdPayAmt2 ) )
		{
			alert("판매수납대상금액과 수납금액이 일치하지 않습니다.");
			return false;          
		}

		//SKT예수급 수납대상금액 validation check
		var iSktTotSaleAmt      = ParseInt( div_sktPrprc.msk_sktTotSaleAmt.Value );
		var iSktPrprcCashPayAmt = ParseInt( ds_payment.GetColumn(0,"skt_prprc_cash_pay_amt") );
		var iSktPrprcPrxpayAmt  = ParseInt( ds_payment.GetColumn(0,"skt_prprc_prxpay_amt") );

		if ( iSktTotSaleAmt != (iSktPrprcCashPayAmt + iSktPrprcPrxpayAmt) )
		{
			alert("예수금수납대상금액과 수납금액이 일치하지 않습니다.");
			return false; 
		}
	}

    return bFlag;
}

function lf_prodCheck(oProdSerNum, oProdMdlCd, oProdMdlNm, oProdSettlCond , sMsgCl)
{
    var sMsg = "";
    if ( !uf_IsNull(oProdserNum.Text) && !uf_IsNull(oProdMdlCd.Text) && !uf_IsNull(oProdMdlNm.Text) )
    {
        if ( uf_IsNull(oProdsettlCond.value) )
        {
            sMsg = sMsgCl + " " + "결제조건을 선택해 주십시요.";
        }
    }
    else
    {
        if (uf_IsNull(oProdserNum.Text) && uf_IsNull(oProdMdlCd.Text) && uf_IsNull(oProdMdlNm.Text))
        {
            sMsg = "N";
        }
        else if (uf_IsNull(oProdSerNum.Text))
        {
            sMsg = sMsgCl + " "  + "일련번호를 입력해 주십시요.";
        }
        else if (uf_IsNull(oProdMdlCd.Text))
        {
            sMsg = sMsgCl + " "  + "모델 코드를 입력해 주십시요.";
        }
        else if (uf_IsNull(oProdMdlNm.Text))
        {
            sMsg = sMsgCl + " "  + "모델명을 입력해 주십시요.";
        }
        else
        {
            sMsg = "";
        }
    }
    return sMsg;
}

/*******************************************************************************
 * @description    : 단말기 관련 validation check
*******************************************************************************/
