*******************************************************************************/
function lf_usimValidatinCheck()
{
    var bFlag = true;
/* 
    if ( uf_IsNull(div_usimInfo.edt_usimMdlCd.Text) || uf_IsNull(div_usimInfo.edt_usimSerNum.Text) )
    {
