		cf_setParam(ds_param, "crdtcrd_foi_cl2"        , ds_olinePayment.GetColumn(0,"crdtcrd_foi_cl2") );
		cf_setParam(ds_param, "crdtcrd_pay_amt2"       , ds_olinePayment.GetColumn(0,"crdtcrd_pay_amt2") );

