function lf_prodSearch(sProdCl)
{
    var sProdCd = "";
    var sProdNm = "";

    if (sProdCl == "1")
    {
        sProdCd = "div_eqpInfo.edt_eqpMdlCd";
        sProdNm = "div_eqpInfo.edt_eqpMdlNm";

        lf_getPordColorList(div_eqpInfo.edt_eqpMdlCd.Text);
    }
    else
    {
        sProdCd = "div_usimInfo.edt_usimMdlCd";
        sProdNm = "div_usimInfo.edt_usimMdlNm";
    }

    cf_commPopProd(sProdCd, sProdNm, 13);    
}

