function div_dealCoInfo_cmb_slCl_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    FV_SL_CL = strCode;

    lf_getSlNetCd(strCode);

	div_dealCoInfo.edt_salePlcNm.Text = "";
	div_dealCoInfo.edt_salePlcUkeyCd.Text = "";
	div_dealCoInfo.edt_salePlcSubCd.Text = "";
	div_dealCoInfo.edt_salePlcChannelCd.text = "";
	div_dealCoInfo.edt_salePlc.Text = "";

	div_saleInfo.cmb_slNetCd.Index = 0;

	lf_visible(FV_SL_CL);

}

function lf_getSlNetCd(sVal)
{
    http.Sync = true;
	// 공통 코드를 위한 데이타셋 설정
	cf_setCommonCodeDsEnv();

	cf_setCommonCode("ds_commSlNetCd"               , "ZPOL_C_001"+sVal);          //판매정보 - 영업망

	// 설정된 코드를 기반으로 공통 코드를 추가 및 데이타 바인딩
	cf_checkCommonCode();
	http.Sync = false;	
}

function lf_visible(sSlCl)
{

	if ( sSlCl == "01" )
	{
		//판매담당자
		div_dealCoInfo.sti_saleChgrg.Visible = true;
		div_dealCoInfo.cmb_saleChgrgId.Visible = true;

		//온라인거래처
		div_dealCoInfo.sti_onlinePlc.Visible = false;
		div_dealCoInfo.edt_onlinePlcNm.Visible = false;
		div_dealCoInfo.btn_onlinePlc.Visible = false;
		div_dealCoInfo.edt_onlinePlc.Visible = false;

		//영업승인번호
		div_saleInfo.sti_saleAprvNum.Visible = false;
		div_saleInfo.edt_saleAprvNum.Visible = false;
	}
	else if (sSlCl == "02")
	{
		//판매담당자
		div_dealCoInfo.sti_saleChgrg.Visible = false;
		div_dealCoInfo.cmb_saleChgrgId.Visible = false;

		//온라인거래처
		div_dealCoInfo.sti_onlinePlc.Visible = false;
		div_dealCoInfo.edt_onlinePlcNm.Visible = false;
		div_dealCoInfo.btn_onlinePlc.Visible = false;
		div_dealCoInfo.edt_onlinePlc.Visible = false;

		//영업승인번호
		div_saleInfo.sti_saleAprvNum.Visible = true;
		div_saleInfo.edt_saleAprvNum.Visible = true;
	}
	else if (sSlCl == "03")
	{
		//판매담당자
		div_dealCoInfo.sti_saleChgrg.Visible = true;
		div_dealCoInfo.cmb_saleChgrgId.Visible = true;

		//온라인거래처
		div_dealCoInfo.sti_onlinePlc.Visible = true;
		div_dealCoInfo.edt_onlinePlcNm.Visible = true;
		div_dealCoInfo.btn_onlinePlc.Visible = true;
		div_dealCoInfo.edt_onlinePlc.Visible = true;

		//영업승인번호
		div_saleInfo.sti_saleAprvNum.Visible = false;
		div_saleInfo.edt_saleAprvNum.Visible = false;
	}                
	else if (sSlCl == "04")
	{
		//판매담당자
		div_dealCoInfo.sti_saleChgrg.Visible = false;
		div_dealCoInfo.cmb_saleChgrgId.Visible = false;

		//온라인거래처
		div_dealCoInfo.sti_onlinePlc.Visible = true;
		div_dealCoInfo.edt_onlinePlcNm.Visible = true;
		div_dealCoInfo.btn_onlinePlc.Visible = true;
		div_dealCoInfo.edt_onlinePlc.Visible = true;

		//영업승인번호
		div_saleInfo.sti_saleAprvNum.Visible = true;
		div_saleInfo.edt_saleAprvNum.Visible = true;
	}                      
}


