	if ( nChar == 13 )
	{
	    div_dealCoInfo.btn_onlinePlc.Click();
	}
}

function div_dealCoInfo_btn_onlinePlc_OnClick(obj)
{
    var sSaleDtm = div_dealCoInfo.cal_saleDtm.Value + div_dealCoInfo.edt_saleDtmHh.Text + div_dealCoInfo.edt_saleDtmMm.Text;
	var sArg  = "deal_co_cl1="+quote("50");
		//sArg += " deal_status="+quote("3"); 	//옵션 (거래처 상태) - 1: 수납정지 N  , 2: 출고정지 N , 3: 판매정지 N 
		//sArg += " pos_agency="+quote(gds_session.GetColumn(0,"posAgencyId")); // 옵션	    
		sArg += " eff_dtm="+quote(sSaleDtm);

	cf_callDealPop(sArg, div_dealCoInfo, "edt_onlinePlcNm,edt_onlinePlc");	
}


function lf_getPordColorList(sMdlCd)
{
    ds_input.clear();

	cf_setParam(ds_input, "prod_cd" , sMdlCd);   //Model code

	var sSvcID = "getProdColorList";
	var sInDs  = "nc_input_fieldDs=ds_input";
	var sOutDs = "ds_prodColorList=List";
	var sArg   = "sktst.sal.SCO#getProdColorList";

	http.Sync = true;						   
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);	
	http.Sync = false;
}

function btn_dummyDis_OnClick(obj)
{
    var sSaleDt = div_dealCoInfo.cal_saleDtm.Value;
    var sSalePlc = div_dealCoInfo.edt_salePlc.Text;
    var sSalePlcNm = div_dealCoInfo.edt_salePlcNm.Text;
    var sDisHldPlc = div_dealCoInfo.edt_disHldPlc.Text;
    var sFlag = "true";

    var sChkEqpYn = "N";
    var sChkUsimYn = "N";

    if ( FV_CHK_EQP_YN == "Y" )
    {
		if ( uf_IsNull(sSaleDt) )
		{
			alert("판매일자를 입력해 주십시요.");
			sFlag = "false";   
		}
		else if ( uf_IsNull(sSalePlc) || uf_IsNull(sSalePlcNm) )
		{
			alert("판매처를 입력해 주십시요.");
			sFlag = "false";   
		}
		else if ( uf_IsNull(div_eqpInfo.cmb_eqpColorCd.Value) )
		{
			alert("단말기 색상을 선택해 주십시요.");
			sFlag = "false";
		}
	}

    if ( sFlag == "true" )
    {
        if ( FV_CHK_EQP_YN == "Y" )
        {
			cf_setParam(ds_dummyEqpInfo, "sale_dt"         , sSaleDt);
			cf_setParam(ds_dummyEqpInfo, "prod_cl"         , "1" );
			cf_setParam(ds_dummyEqpInfo, "mdl_cd"          , div_eqpInfo.edt_eqpMdlCd.Text );
			cf_setParam(ds_dummyEqpInfo, "mdl_nm"          , div_eqpInfo.edt_eqpMdlNm.Text );
			cf_setParam(ds_dummyEqpInfo, "ser_num"         , div_eqpInfo.edt_eqpSerNum.Text );
			cf_setParam(ds_dummyEqpInfo, "color_cd"        , div_eqpInfo.cmb_eqpColorCd.Value );
			cf_setParam(ds_dummyEqpInfo, "color_nm"        , div_eqpInfo.cmb_eqpColorCd.Text );
			cf_setParam(ds_dummyEqpInfo, "sale_plc"        , sSalePlc );	
			cf_setParam(ds_dummyEqpInfo, "sale_plc_nm"     , sSalePlcNm );	
			cf_setParam(ds_dummyEqpInfo, "dis_hld_plc"     , sDisHldPlc );	
//			cf_setParam(ds_eqpParam, "in_fix_dt"       , ds_dummyEqpInfo.GetColumn(0,"in_fix_dt") );	
//			cf_setParam(ds_eqpParam, "in_plc"          , ds_dummyEqpInfo.GetColumn(0,"in_plc") );	
//			cf_setParam(ds_eqpParam, "in_plc_nm"       , ds_dummyEqpInfo.GetColumn(0,"in_plc_nm") );	
//			cf_setParam(ds_eqpParam, "prchs_plc"       , ds_dummyEqpInfo.GetColumn(0,"prchs_plc") );	
//			cf_setParam(ds_eqpParam, "prchs_plc_nm"    , ds_dummyEqpInfo.GetColumn(0,"prchs_plc_nm") );	
//			cf_setParam(ds_eqpParam, "out_unit_prc"    , ds_dummyEqpInfo.GetColumn(0,"out_unit_prc") );	
//			cf_setParam(ds_eqpParam, "rmks"            , ds_dummyEqpInfo.GetColumn(0,"rmks") );	
	    }

	    if ( FV_CHK_USIM_YN == "Y" )
	    {
			cf_setParam(ds_dummyUsimInfo, "sale_dt"        , sSaleDt);
			cf_setParam(ds_dummyUsimInfo, "prod_cl"        , "2" );
			cf_setParam(ds_dummyUsimInfo, "mdl_cd"         , div_usimInfo.edt_usimMdlCd.Text );
			cf_setParam(ds_dummyUsimInfo, "mdl_nm"         , div_usimInfo.edt_usimMdlNm.Text );
			cf_setParam(ds_dummyUsimInfo, "ser_num"        , div_usimInfo.edt_usimSerNum.Text );
			cf_setParam(ds_dummyUsimInfo, "color_cd"       , "99" );
			cf_setParam(ds_dummyUsimInfo, "color_nm"       , "" );
			cf_setParam(ds_dummyUsimInfo, "sale_plc"       , sSalePlc );	
			cf_setParam(ds_dummyUsimInfo, "sale_plc_nm"    , sSalePlcNm );	
			cf_setParam(ds_dummyUsimInfo, "dis_hld_plc"    , sDisHldPlc );	
//			cf_setParam(ds_dummyUsimInfo, "in_fix_dt"      , ds_dummyUsimInfo.GetColumn(0,"in_fix_dt") );	
//			cf_setParam(ds_dummyUsimInfo, "in_plc"         , ds_dummyUsimInfo.GetColumn(0,"in_plc") );	
//			cf_setParam(ds_dummyUsimInfo, "in_plc_nm"      , ds_dummyUsimInfo.GetColumn(0,"in_plc_nm") );	
//			cf_setParam(ds_dummyUsimInfo, "prchs_plc"      , ds_dummyUsimInfo.GetColumn(0,"prchs_plc") );	
//			cf_setParam(ds_dummyUsimInfo, "prchs_plc_nm"   , ds_dummyUsimInfo.GetColumn(0,"prchs_plc_nm") );	
//			cf_setParam(ds_dummyUsimInfo, "out_unit_prc"   , ds_dummyUsimInfo.GetColumn(0,"out_unit_prc") );	
//			cf_setParam(ds_dummyUsimInfo, "rmks"           , ds_dummyUsimInfo.GetColumn(0,"rmks") );	
		}

		var sArg  = "excuteMethod="  + quote("lf_DummyDisPopupClose");
		sArg += " p_posAgencyCd=" + quote(div_dealCoInfo.edt_posAgencyCd.Text);
		sArg += " p_chkEqpYn="    + quote(FV_CHK_EQP_YN);
		sArg += " p_chkUsimYn="   + quote(FV_CHK_USIM_YN);
		sArg += " p_salePlc="     + quote(sSalePlc);
		sArg += " p_salePlcNm="   + quote(sSalePlcNm);
		sArg += " p_disHldPlc="   + quote(sDisHldPlc);
		sArg += " p_saleDt="      + quote(sSaleDt);
		sArg += " p_saleTm="      + quote(div_dealCoInfo.edt_saleDtmHh.Text + div_dealCoInfo.edt_saleDtmMm.Text + "00" );

		// 팝업 함수
		dialog("SAL::SALDGS00200.xml", sArg ,467, 312);	
	}
	return;
}

