/*******************************************************************************
 * @description    : SKT예수금 변경시 수납정보의 SKT예수금 금액 변경
*******************************************************************************/
function f_sktPrprcAmt()
{
    var iAmt           = 0;
    var iScrbFee       = 0;
    var iMnpCmms       = 0;
    var iFrgGrtInsuFee = ParseInt(div_bfSktPrprc.msk_frgGrtInsuFee.Value);

    //가입비는 즉납 : 01 일때만 수납
//    if ( ds_generalSaleIfInfo.GetColumn(0,"scrb_fee_cl") == "01" )
    if ( ds_sktPrprc.GetColumn(0,"scrb_fee_cl") == "01" )
    {
        iScrbFee = ParseInt(div_bfSktPrprc.msk_scrbFee.Value);
    }

    //번호이동 수수료는 완납 : 1 일때만 수납
//    if ( ds_generalSaleIfInfo.GetColumn(0,"mnp_cmms_cl") == "1" )
    if ( ds_sktPrprc.GetColumn(0,"mnp_cmms_cl") == "1" )
    {
        iMnpCmms = ParseInt(div_bfSktPrprc.msk_mnpCmms.Value);
    }

    iAmt = iScrbFee + iMnpCmms + iFrgGrtInsuFee;

    div_bfSktPrprc.msk_sktTotSaleAmt.Value = iAmt;
    div_bfSktPrprc.msk_payAmt.Value = iAmt;

    //예수금 수납금액 합
    var iCashAmt   = ParseInt(div_bfSktPrprc.msk_cashPayAmt.Value);
    var iPrxpayAmt = ParseInt(div_bfSktPrprc.msk_prxpayAmt.Value);

    div_bfSktPrprc.msk_paySumamt.Value = iCashAmt + iPrxpayAmt;
}

/*******************************************************************************
 * @description    : SKT예수금 변경시 수납정보의 SKT예수금 금액 변경
*******************************************************************************/
function f_afSktPrprcAmt()
{
    var iAmt           = 0;
    var iScrbFee       = 0;
    var iMnpCmms       = 0;
    var iFrgGrtInsuFee = ParseInt(div_afSktPrprc.msk_frgGrtInsuFee.Value);

    iScrbFee = 0;

    //번호이동 수수료는 완납 : 1 일때만 수납
//    if ( ds_generalSaleIfInfo.GetColumn(0,"mnp_cmms_cl") == "1" )
    if ( ds_sktPrprc.GetColumn(0,"mnp_cmms_cl") == "1" )
    {
        iMnpCmms = ParseInt(div_afSktPrprc.msk_mnpCmms.Value);
    }

    iAmt = iScrbFee + iMnpCmms + iFrgGrtInsuFee;

    div_afSktPrprc.msk_sktTotSaleAmt.Value = iAmt;
    div_afSktPrprc.msk_payAmt.Value = iAmt;
}


function f_saveEnd()
{

    //f_divEnable("01");
    cf_callnClose("lf_dialogClose", "");
}


/*******************************************************************************
 * @description    : oParam1 값이 없으면 oParam2,oParam3,oParam4 "" 처리
*******************************************************************************/
function f_clearNm(oParam1, oParam2, oParam3, oParam4)
{
     if ( uf_IsNull(oParam1.Text) )
     {
         oParam2.Text = "";

         if ( !uf_IsNull(oParam3) )
         {
             oParam3.Text = "";
             oParam4.Text = "";
         }
     }
}

/*******************************************************************************
 * @description    : 일반판매 원본DataSet Backup
*******************************************************************************/
function f_backUpDataSet()
{
	//원본 Data Backup

	//일반판매
	cf_setOrgDs(ds_generalSale);
	//단말기판매
	cf_setOrgDs(ds_equipmentSale);
	//USIM판매
	cf_setOrgDs(ds_usimSale);
	//부가서비스판매
	cf_setOrgDs(ds_suplservice);
	//중고반납기기
	cf_setOrgDs(ds_oldrtnEquipment);
	//할부매출
	cf_setOrgDs(ds_allotSale);
	//SKT예수금
	cf_setOrgDs(ds_sktPrprc);
	//약정보조금
	cf_setOrgDs(ds_agreementAstamt);
	//OCB/MCARD
	cf_setOrgDs(ds_ocbmcard);
	//현금매출
	cf_setOrgDs(ds_cashSale);
	//수납정보
	cf_setOrgDs(ds_payment);

	return;
}

/*******************************************************************************
 * @description    : 일반판매 원본DataSet 변경된 값이 있는지 Check
*******************************************************************************/
function f_chgChkDataSet()
{
    //판매변경구분 변경 되었을대...........
    //무조건 걸리므로 ......

	var sMsg = "";

	//일반판매
	if (cf_checkDsChanging(ds_generalSale) <= 4)
	{
	    sMsg = "변경된 일반판매 정보가 있습니다.";
	    return sMsg;
	}

	//단말기판매
	if (cf_checkDsChanging(ds_equipmentSale) <= 4)
	{
	    sMsg = "변경된 단말기 정보가 있습니다.";
	    return sMsg;
	}

	//USIM판매
	if (cf_checkDsChanging(ds_usimSale) <= 4)
	{
	    sMsg = "변경된 USIM 정보가 있습니다.";
	    return sMsg;
	}

	//부가상품판매
	if (cf_checkDsChanging(ds_suplservice) <= 4)
	{
	    sMsg = "변경된 부가상품 정보가 있습니다.";
	    return sMsg;
	}

	//중고반납기기
	if (cf_checkDsChanging(ds_oldrtnEquipment) <= 4)
	{
	    sMsg = "변경된 중고반납기기 정보가 있습니다.";
	    return sMsg;
	}

	//할부매출
	if (cf_checkDsChanging(ds_allotSale) <= 4)
	{
	    sMsg = "변경된 할부판매가 정보가 있습니다.";
	    return sMsg;
	}

	//SKT예수금
	if (cf_checkDsChanging(ds_sktPrprc) <= 4)
	{
	    sMsg = "변경된 SKT예수금 정보가 있습니다.";
	    return sMsg;
	}

	//약정보조금
	if (cf_checkDsChanging(ds_agreementAstamt) <= 4)
	{
	    sMsg = "변경된 약정보조금 정보가 있습니다.";
	    return sMsg;
	}

	//OCB/MCARD
	if (cf_checkDsChanging(ds_ocbmcard) <= 4)
	{
	    sMsg = "변경된 OCB/MCARD 정보가 있습니다.";
	    return sMsg;
	}

	//현금매출
	if (cf_checkDsChanging(ds_cashSale) <= 4)
	{
	    sMsg = "변경된 현금판매가 정보가 있습니다.";
	    return sMsg;
	}

	//수납정보
	if (cf_checkDsChanging(ds_payment) <= 4)
	{
	    sMsg = "변경된 수납 정보가 있습니다.";
	    return sMsg;
	}

	return true;
}

/*******************************************************************************
 * @description    : 공통 닫기 함수
*******************************************************************************/
function SALMGS00400_OnUnloadCompleted(obj)
{
    var sMsg = f_chgChkDataSet();
    var nResult;
}

/*******************************************************************************
 * @description    : 판매변경구분 변경시 판매변경일시,판매변경처,판매변경자를
                     판매처로 기본 셋팅
*******************************************************************************/
function f_setIfSaleChg()
{
	var sSaleChgDtm = ds_dsNetChgIfInfo.GetColumn(0,"proc_dt")+ds_dsNetChgIfInfo.GetColumn(0,"proc_tm");

	//판매변경일시
	sf_setDateByDataSet("div_prodExchange.cal_saleChgDtm","div_prodExchange.edt_saleChgDtmHh", "div_prodExchange.edt_saleChgDtmMm", sSaleChgDtm);

	div_prodExchange.edt_saleChgPlcNm.Text        = "";
	div_prodExchange.edt_saleChgPlc.Text          = ds_dsNetChgIfInfo.GetColumn(0,"sale_plc");
	div_prodExchange.edt_saleChgPlcUkeyCd.Text    = ds_dsNetChgIfInfo.GetColumn(0,"ukey_agency_cd");
	div_prodExchange.edt_saleChgPlcChannelCd.Text = ds_dsNetChgIfInfo.GetColumn(0,"ukey_channel_cd");
	div_prodExchange.edt_saleChgPlcSubCd.Text     = ds_dsNetChgIfInfo.GetColumn(0,"ukey_sub_cd");

	div_prodExchange.btn_saleChgPlc.Click();

    div_prodExchange.cmb_saleChgUserId.Value = ds_dsNetChgIfInfo.GetColumn(0,"proc_chgrg_id");
}

function btn_close_OnClick(obj)
{
    if (FV_IF_PROC_CL == "4")
    {
        parent.lf_updateModUserId("C");
        parent.lf_close();
        return;
    }

	this.Close();
}


function div_dealCoInfo_edt_saleDtmHh_OnKillFocus(obj)
{
	if ( (div_dealCoInfo.cal_svcDtm.value + div_dealCoInfo.edt_svcDtmHh.Text)
		< (div_dealCoInfo.cal_saleDtm.Value + obj.Value) )
	{
		alert("판매일시는 개통일시와 같거나 이전이어야 합니다.");
		obj.SetFocus();
		return;
	}
}

function div_dealCoInfo_edt_saleDtmMm_OnKillFocus(obj)
{
	if (div_dealCoInfo.cal_svcDtm.value + div_dealCoInfo.edt_svcDtmHh.Text + div_dealCoInfo.edt_svcDtmMm.Text
		< div_dealCoInfo.cal_saleDtm.Value + div_dealCoInfo.edt_saleDtmHh.Text + obj.Value )
	{
		alert("판매일시는 개통일시와 같거나 이전이어야 합니다.");
		obj.SetFocus();
		return;
	}
}

function div_dealCoInfo_cal_saleDtm_OnKillFocus(obj)
{

    if ( div_dealCoInfo.cal_svcDtm.Value < obj.value )
    {
        alert("판매일자는 개통일자와 같거나 이전이어야 합니다.");
        obj.Setfocus();
        return;
    }
}

/*******************************************************************************
 * @description    : 수납합계 - SKT예수금
 ******************************************************************************/
function lf_sktSumPayment()
{
	var iCashPayAmt  = 0;
	var iPrxpayAmt   = 0;
	var iPaySumAmt   = 0;

	iCashPayAmt      = ParseInt(div_afSktPrprc.msk_cashPayAmt.Value );
	iPrxpayAmt       = ParseInt(div_afSktPrprc.msk_prxpayAmt.Value );

	iPaySumAmt = iCashPayAmt + iPrxpayAmt;

	div_afSktPrprc.msk_paySumAmt.Value = iPaySumAmt;
}


function div_afSktPrprc_msk_cashPayAmt_OnChanged(obj,strText)
{
	lf_sktSumPayment();
}

function div_afSktPrprc_msk_cashPayAmt_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if ( nChar == 13 )
	{
	    lf_sktSumPayment();
	}
}

function div_afSktPrprc_msk_prxpayAmt_OnChanged(obj,strText)
{
	lf_sktSumPayment();
}

function div_afSktPrprc_msk_prxpayAmt_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if ( nChar == 13 )
	{
	    lf_sktSumPayment();
	}
}

/*******************************************************************************
 * @description    : 변경 후 유통망 처리
 ******************************************************************************/
function lf_chgProcess()
{
    /****************************************************************************************
    * U.Key 전문                               |        PS.Key 변경 후
    * 유통망(약정구분)        처리구분         | 유통망           약정기간        약정금액
    *-------------------------------------------------------------------------------------
    * 1 - 할부지원            01 - 취소        | 0 - 무약정       00 - 없음
    *                         02 - 취소철회    | 1 - T할부지원    00 - 없음       I/F 금액
    *  2 - T기본약정          00 - 가입        | 2 - T기본약정    I/F 개월 수     I/F 금액
    *                         02 - 해약        | 0 - 무약정       00 - 없음
    *                         03 - 해약철회    | 2 - T기본약정    I/F 개월 수     I/F 금액
    *                         04 - 취소        | 0 - 무약정       00 - 없음
    *                         05 - 취소철회    | 2 - T기본약정    I/F 개월 수     I/F 금액
    *****************************************************************************************/

    var sProcCl           = "";
    var sAfDsNetCd        = ds_dsNetChgIfInfo.GetColumn(0,"ds_net_cd");
    var sAfDsNetChgCl     = ds_dsNetChgIfInfo.GetColumn(0,"ds_net_chg_cl");
    var sAfAgrmtPrdCd     = ds_dsNetChgIfInfo.GetColumn(0,"agrmt_prd_cd");
    var sAfAgrmtAstamt    = ds_dsNetChgIfInfo.GetColumn(0,"agrmt_astamt");
    var sAfRtnAgrmtAstamt = ds_dsNetChgIfInfo.GetColumn(0,"retention_agrmt_astamt");

    if ( sAfDsNetCd == "1" )
    {
         switch(sAfDsNetChgCl)
         {
             case "00": //가입
                 sProcCl = "1";
                 break;
             case "01": //취소
                 sProcCl = "0";
                 break;
             case "02": //취소철회
                 sProcCl = "1";
                 break;
             default:
                 sProcCl = "";
                 break;
         }
    }
    else if ( sAfDsNetCd == "2" )
    {
         switch(sAfDsNetChgCl)
         {
             case "00": //가입
                 sProcCl = "1";
                 break;
             case "02": //해약
                 sProcCl = "0";
                 break;
             case "03": //해약철회
                 sProcCl = "1";
                 break;
             case "04": //취소
                 sProcCl = "0";
                 break;
             case "05": //취소철회
                 sProcCl = "1";
                 break;
             default:
                 sProcCl = "";
                 break;
         }
    }
    else
    {
        sProcCl = "";
    }

    FV_PROC_CL = sProcCl;

    lf_dsNetChgCl(sProcCl, sAfDsNetCd, sAfAgrmtPrdCd, sAfAgrmtAstamt, sAfRtnAgrmtAstamt);

    // 소매, 온.소매
    // 1. 유통망 변경.
    // 2. 약정개월 수 변경.
    // 3. PS현금판매가 변경.
    // 4. 현금판매가 변경 없음.
    // 5. 수납정보 변경 없음.

    // 도매, 온.도매
    // 1. 유통망 변경.
    // 2. 약정개월 수 변경.
    // 3. PS현금판매가 변경.
    // 4. 현금판매가 변경 - 판매가격표 기준.
    // 5. 수납정보 변경 없음.

}

/*******************************************************************************
 * @description    : PS.Key 유통망 변경 후 값 셋팅
 *                   1. 유통망 변경.
 *                   2. 약정개월 수 변경.
 *                   3. 현금판매가 변경 없음.
 *                   4. 수납정보 변경 없음.
 *
 * @sProcCl        : 0 - 취소, 1 - 기타...
 * @sBfDsNetCd     : Ukey I/F 유통망
 * @sBfAgrmtPrdCd  : Ukey I/F 약정기간
 * @sBfAgrmtAstamt : Ukey I/F 약정금액
 ******************************************************************************/
function lf_dsNetChgCl(sProcCl, sAfDsNetCd, sAfAgrmtPrdCd, sAfAgrmtAstamt, sAfRtnAgrmtAstamt)
{
    // 1.유통망변경, 2.약정개월 수 변경
    switch(sProcCl)
    {
        case "0":
	        cf_setParam(ds_afDsNet, "ds_net_cd"     , "0" );    //유통망   - 0 : 무약정
	        cf_setParam(ds_afDsNet, "agrmt_prd_cd"  , "00");    //약정기간 - 00 : 없음
	        cf_setParam(ds_afDsNet, "agrmt_astamt"  , "0" );    //약정금액 - 0
	        cf_setParam(ds_afDsNet, "retention_agrmt_astamt"  , "0" );
            break;
        case "1":
            if ( sAfDsNetCd == "1" )
            {
                sAfAgrmtPrdCd = "00";
            }

	        cf_setParam(ds_afDsNet, "ds_net_cd"     , sAfDsNetCd    );   //유통망   - Ukey I/F 유통망
	        cf_setParam(ds_afDsNet, "agrmt_prd_cd"  , sAfAgrmtPrdCd );   //약정기간 - 유통망(약정구분) 1 - 00,  2 - Ukey I/F 약정기간
	        cf_setParam(ds_afDsNet, "agrmt_astamt"  , sAfAgrmtAstamt);   //약정금액 - Ukey I/F 약정금액
	        cf_setParam(ds_afDsNet, "retention_agrmt_astamt"  , sAfRtnAgrmtAstamt );
            break;
        default:
	        cf_setParam(ds_afDsNet, "ds_net_cd"     , "" );
	        cf_setParam(ds_afDsNet, "agrmt_prd_cd"  , "" );
	        cf_setParam(ds_afDsNet, "agrmt_astamt"  , "" );
	        cf_setParam(ds_afDsNet, "retention_agrmt_astamt"  , "" );
            break;
    }

}
