/*******************************************************************************
 * @description    : 정책 부가상품 리스트 취득 
*******************************************************************************/
function f_getSupProdAll()
{

	ds_condition.Clear();

	// 넘길 파라메터 설정
	cf_setParam(ds_condition, "sale_dtm"     , div_general.cal_saleDtm.Value); 
	cf_setParam(ds_condition, "org_id"       , FV_SUP_PROD_ORG_ID); 
	cf_setParam(ds_condition, "sl_net_cd"    , div_saleInfo.cmb_slNetCd.Value ); 
	cf_setParam(ds_condition, "ds_net_cd"    , div_saleInfo.cmb_dsNetCd.Value ); 
	cf_setParam(ds_condition, "sale_typ_cd"  , div_saleInfo.cmb_saleDtlTyp.Value ); 
	cf_setParam(ds_condition, "prod_cl"      , div_saleInfo.cmb_supCl.Value ); 


	// 트랜잭션 설정
	var sSvcID = "svcSupProdAll";
	var sInDs  = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_supSvc=SupSvcList";
	var sArg   = "sktst.sal.SCO#getSupSvcCdList";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);	
}

/*******************************************************************************
 * @description    :  정산처 취득
*******************************************************************************/
function f_getStlPlc()
{
	var sPlc     = div_general.edt_salePlc.Text;
	var sSaleDtm = f_getDate("SALE");

	if( uf_isNull(sPlc) == true) 
	{
//		trace('판매처가 입력되지 않았습니다. : ' + sPlc);
		return;
	}

//trace("f_getStlPlc(" + sPlc + "/" + sSaleDtm + ")");		

	if( uf_isNull(sSaleDtm) == true) return;

	sf_getStlPlc( "ds_condition", "ds_stlPlc", sPlc, sSaleDtm );
}


