/*******************************************************************************
 * @description    : 화면 로딩시 
*******************************************************************************/
function f_init()
{
	sf_setDate("div_general.cal_saleDtm","div_general.edt_saleDtmHH","div_general.edt_saleDtmMM");
	sf_setDate("div_general.cal_svcDtm","div_general.edt_svcDtmHH","div_general.edt_svcDtmMM");
	ds_masterInfo.AddRow();

	f_setAuth();
	f_setCombo();

	cf_setOrgDs(ds_masterInfo);
}


/*******************************************************************************
 * @description    : 권한 설정
*******************************************************************************/
function f_setAuth()
{
	f_getSession();

	uf_enableDealByAuth("div_general" ,"edt_salePlcNm,edt_salePlc,btn_salePlc",",edt_salePlcSubCd,edt_salePlcChannelCd");

	// 로그인 사용자
	f_getStlPlc();
	http.Sync = true;	
	f_getSaleChgrgId();
	if(uf_getAuthCode() == 2)
	{			
		uf_enableChgrgByAuth(div_general.cmb_saleChgrgId);
	}
	else
	{
		f_initSaleChgrgId();
	}
	http.Sync = false;

}



/*******************************************************************************
 * @description    : 글로벌 섹션 정보
*******************************************************************************/
function f_getSession()
{
	FV_USER_ID        = gds_session.GetColumn(0, "loginid");	// 사용자ID
	FV_USER_NM        = gds_session.GetColumn(0, "usernm");		// 사용자명

	FV_ORG_ID         = gds_session.GetColumn(0, "orgid");				// 소속 
	FV_ORG_NM         = gds_session.GetColumn(0, "orgnm");				// 소속명
	FV_ORG_CL         = gds_session.GetColumn(0, "orgcl");				// 소속구분
	FV_AGENCY_ID      = gds_session.GetColumn(0, "posAgencyId");        // 소속 대리점ID
	FV_AGENCY_NM      = gds_session.GetColumn(0, "posAgencyNm");        // 소속 대리점명
	FV_AGENCY_UKEY_ID = gds_session.GetColumn(0, "posAgencyUkeyId");    // 소속 대리점UKEY ID	

	FV_ORG_AREA_ID            = gds_session.GetColumn(0, "orgareaid");			// 근무지 ID
	FV_ORG_AREA_NM            = gds_session.GetColumn(0, "orgareanm");			// 근무지명
	FV_ORG_AREA_POS_AGENCY_CD = gds_session.GetColumn(0, "orgareaposagencycd");	// 근무지 
	FV_ORG_AREA_UKEY_ID       = gds_session.GetColumn(0, "orgareaukeyid");      // 근무지ukeyid
	FV_ORG_AREA_SUB_CD        = gds_session.GetColumn(0, "orgareaukeysubcd");   // 근무지subcd
	FV_ORG_AREA_CHANNEL_CD    = gds_session.GetColumn(0, "orgareaukeychannelcd");  // 근무지 channelcd	
	FV_USER_GRP               = gds_session.GetColumn(0, "usergrp");			// 유저그룹
	FV_ORG_AREA_CL1           = gds_session.GetColumn(0, "orgareacl1");			// 근무지 구분
	FV_DUTY                   = gds_session.GetColumn(0, "duty");	   		    // 직책		
}


