 * @description    : 콤보박스 설정
*******************************************************************************/
function f_setCombo()
{
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("div_custInfo.cmb_custCl"    , "ZSAL_C_00220", "선택");	// 고객구분		
	cf_setCommonCode("div_saleInfo.cmb_slNetCd"   , "ZPOL_C_00101", "선택");	// 영업망
	cf_setCommonCode("div_saleInfo.cmb_saleDtlTyp", "ZPOL_C_00040", "선택");	// 판매유형
	cf_setCommonCode("div_saleInfo.cmb_dsNetCd"   , "ZPOL_C_00110", "선택");	// 유통망
	cf_setCommonCode("div_saleInfo.cmb_supCl"     , "ZBAS_C_00320", "전체");	// 부가상품
	cf_setCommonCode("ds_supCl"                   , "ZBAS_C_00320");	        // 부가상품
	cf_setCommonCode("ds_idmLmtCd"                , "ZBAS_C_00330");          	// 유치기한
	cf_setCommonCode("ds_saleDtlTyp", "ZPOL_C_00040");	// 판매유형
	cf_checkCommonCode(); 


	//cf_setInitDataForCmb(div_general.cmb_saleChgrgId, "선택");
	cf_setInitDataForCmb(div_general.cmb_svcChgrgId, "선택");
}


/*-----------------------------------------------------------------------
 * Desc   : 좌측버튼 클릭시 이벤트 발생.
 * Auth   : 전승호 (2009.01.15) 
 *------------------------------------------------------------------------*/
function btn_suttleRight_OnClick(obj)
{

	if(ds_supSvc.currow == -1 ){
		alert("조회된 부가서비스가 없습니다.");
		return;
	}

	grd_supSvc.Redraw = false;
	grd_supSvcSelect  = false;

	var sHead = grd_supSvc.GetCellProp("head",0,"text");
	var nTotCnt = ds_supSvc.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_supSvc.GetColumn(nRow,"chk");
	   }else{
			bCheck = ds_supSvc.GetColumn(nRow,"chk");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_supSvc.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			var nRowIdx = ds_supSvcSelect.AddRow();
			ds_supSvcSelect.CopyRow(nRowIdx,ds_supSvc,nRow);
			ds_supSvcSelect.SetColumn(nRowIdx, "chk", "0");

			// 수수료 / 장료금 디폴트 
			ds_supSvcSelect.SetColumn(nRowIdx, "PR_PAY_YN", "Y" );

			ds_supSvc.DeleteRow(nRow);
	   }
	}

	ds_supSvcSelect.Sort("supl_svc_cd");
//	grd_supSvc.SetCellProp("head",0,"text","0");

	grd_supSvc.Redraw = true;
	grd_supSvcSelect = true;
}


