/*******************************************************************************
 * @description    : 콜백
 * @param sSvcID   : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{
//trace("f_callBack(" + sSvcID + ") - FV_JOB :" + FV_JOB );
	switch(sSvcID)
	{
	case "svcSupProdAll":	// 정책 부가서비스 
		if(ds_supSvc.GetRowCount() == 0)
		{
			alert("가입가능한 부가서비스가 존재하지 않습니다.");
		}
		break;

	case "getRegdSupSvc":	// 등록된 부가서비스 

		break;

	case "svcStlPlc":	// 정산처 
//cf_debug("ds_stlPlc");	
		var sStlPlc     = ds_stlPlc.GetColumn(0,"STL_PLC");
		var sStlPlcNm   = ds_stlPlc.GetColumn(0,"STL_PLC_NM");	
		var sUkeyCd     = ds_stlPlc.GetColumn(0,"ukey_agency_cd");
		var sSubCd      = ds_stlPlc.GetColumn(0,"ukey_sub_cd");	
		var sChannelCd  = ds_stlPlc.GetColumn(0,"ukey_channel_cd");	
		var sDealEndYn  = ds_stlPlc.GetColumn(0,"deal_end_yn");		// 거래종료여부
		var sSaleStopYn = ds_stlPlc.GetColumn(0,"sale_stop_yn");	// 판매정지여부     
		var sPosAgenCy  = ds_stlPlc.GetColumn(0,"pos_agency");	// 소속 대리점

		if (sDealEndYn == 'N' && sSaleStopYn == 'N')
		{	// 판매가능한 상태
			btn_save.Enable = true;		
		}
		else
		{	// 판매 불가 상태
			btn_save.Enable = false;
		}

		ds_masterInfo.SetColumn(0, "STL_PLC_NM", sStlPlcNm);
		ds_masterInfo.SetColumn(0, "STL_PLC_UKEY_CD", sUkeyCd);
		ds_masterInfo.SetColumn(0, "STL_PLC_SUB_CD", sSubCd);
		ds_masterInfo.SetColumn(0, "STL_PLC_CHANNEL_CD", sChannelCd);
		ds_masterInfo.SetColumn(0, "STL_PLC", sStlPlc);		

		FV_SUP_PROD_ORG_ID = sPosAgenCy;
		break;

	case "svcSave":
		parent.div_search_btn_search_OnClick();
		Close();
		break;

	case "svcChrgrIdList":
		if(FV_CHGRG_GUBUN == "SALE")
		{
			cf_setInitDataForCmb(div_general.cmb_saleChgrgId, "선택");
		}
		else if(FV_CHGRG_GUBUN == "SVC")
		{
			cf_setInitDataForCmb(div_general.cmb_svcChgrgId, "선택");
		}
		break;
	}
}

/*******************************************************************************
 * @description    :  정책부가서비스와 가입된 부가서비스를 그리드에 표시 처리
*******************************************************************************/
function f_procInitGrid()
{
	if(ds_supSvcRegd.GetCount() < 1 )
	{
		return;
	}

	var nTotCnt = ds_supSvc.GetCount();	// 가입가능한 부가상품
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--)
	{
		var nRegedSupProd = ds_supSvcSelect.GetCount();
		if ( nRegedSupProd > 0 ) nRegedSupProd = nRegedSupProd - 1;

		for (var nIdx = nRegedSupProd ; nIdx>=0 ; nIdx--)
		{
			if (ds_supSvc.GetColumn(nRow,"SUPL_PROD_CD") == ds_supSvcSelect.GetColumn(nIdx,"SUPL_PROD_CD"))
			{
				ds_supSvc.DeleteRow(nRow);
			}
		}
	}
}


/*******************************************************************************
 * @description    :  시간 취득
*******************************************************************************/
function f_getDate(sGubun)
{
	var sDate = "";
	if(sGubun == "SALE")
	{
		sDate = div_general.cal_saleDtm.Value 
		      + div_general.edt_saleDtmHH.Text
		      + div_general.edt_saleDtmMM.Text + "00";		 
	}
	else if(sGubun == "SVC")
	{
		sDate = div_general.cal_svcDtm.Value
		      + div_general.edt_svcDtmHH.Text
		      + div_general.edt_svcDtmMM.Text + "00";
	}

	return sDate;
}


/*******************************************************************************
 * @description    :  시간 취득
*******************************************************************************/
function f_getSvcNum()
{
	var svcNum = div_custInfo.edt_svcNum1.Text 
	           + div_custInfo.edt_svcNum2.Text
	           + div_custInfo.edt_svcNum3.Text;

	return svcNum;
}

/*******************************************************************************
 * @description    :  부가상품조회 유효성
*******************************************************************************/
function f_validProdTrans()
{
	var sSaleDtm    = f_getDate("SALE");
	var sStlPlc     = ds_masterInfo.GetColumn(0, "STL_PLC");
	var sSlNetCd    = ds_masterInfo.GetColumn(0, "SL_NET_CD");
	var sDsNetCd    = ds_masterInfo.GetColumn(0, "DS_NET_CD");
	var sSaleDtlTyp = ds_masterInfo.GetColumn(0, "SALE_DTL_TYP");

	if(uf_isNull(sSaleDtm) == true)
	{
		alert(cf_getMessage(MSG_00083, "판매일자"));
		return false;
	}
//trace("f_validProdTrans:sSaleDtm:"+sSaleDtm+"/sSlNetCd:"+sSlNetCd+"/sDsNetCd:"+sDsNetCd+"/sSaleDtlTyp:"+sSaleDtlTyp+"/FV_SUP_PROD_ORG_ID:"+FV_SUP_PROD_ORG_ID );

	if(uf_isNull(sSlNetCd) == true)    return false;
	if(uf_isNull(sDsNetCd) == true)    return false;
	if(uf_isNull(sSaleDtlTyp) == true) return false;
	if( uf_isNull(FV_SUP_PROD_ORG_ID) == true) return false;

	return true;
}


/*******************************************************************************
 * @description    :  날짜 유효성검사
*******************************************************************************/
function f_validDate()
{
//trace("f_validDate()");
	// 마스터 정보 유효성 검사
	var sSaleDtm = substr(f_getDate("SALE"),0,12);
	var sSvcDtm  = substr(f_getDate("SVC"),0,12);
	// 판매일자 체크 
	if(uf_isNull(sSaleDtm) == true)
	{
		alert(cf_getMessage(MSG_00083, "판매일자"));
		div_general.cal_saleDtm.SetFocus();
		return false;	
	}

	if(uf_CheckDate( sSaleDtm ,"YYYYMMDDHH24MI")== false)
	{	// MSG_00061: 입력 오류입니다. 입력 정보를 확인 하십시오 
		alert(cf_getMessage(MSG_00061, "판매일자"));
		return false;
	}

	var sNowDate = substr(getDate(), 0, 12);
	var bRetDate = sf_compare(sSaleDtm, sNowDate);

	if( bRetDate == -1)
	{	// MSG_00094 : 이전 날짜만 입력이 가능합니다.
		alert(cf_getMessage(MSG_00094, "판매일자;현재날짜"));
		div_general.cal_saleDtm.SetFocus();
		return false;
	}

	//####################################
	// 개통일자 체크 
	if(uf_isNull(sSvcDtm) == true)
	{
		alert(cf_getMessage(MSG_00083, "개통일자"));
		div_general.cal_svcDtm.SetFocus();
		return false;	
	}

	if(uf_CheckDate( sSvcDtm ,"YYYYMMDDHH24MI")== false)
	{	// MSG_00061: 입력 오류입니다. 입력 정보를 확인 하십시오 
		alert(cf_getMessage(MSG_00061, "개통일자"));
		return false;
	}

	bRetDate = sf_compare(sSvcDtm, sNowDate);

	if( bRetDate == -1)
	{	// MSG_00094 : 이전 날짜만 입력이 가능합니다.
		alert(cf_getMessage(MSG_00094, "개통일자;현재날짜"));
		div_general.cal_svcDtm.SetFocus();
		return false;
	}

	return true;
}


/*******************************************************************************
 * @description    :  저장시 유효성 검사
*******************************************************************************/
function f_validSave()
{
	// 마감월 체크
	cf_CheckCloseDay('02',div_general.cal_saleDtm.Value, "판매일자"); // 03 정산
	if(FV_BOOLEAN_CLOSE) return false; // 입력한 일자가 마감 되었을 경우 '1'(true) 리턴.

	if(f_validDate() == false) return false;

	var sSalePlc     = div_general.edt_salePlc.Text;
	var sSvcPlc      = div_general.edt_svcPlc.Text;
	var sSaleChgrgId = div_general.cmb_saleChgrgId.Value;
	var sSvcChgrgId  = div_general.cmb_svcChgrgId.Value;

	if(uf_isNull(sSalePlc) == true)
	{
		alert(cf_getMessage(MSG_00083, "판매처"));
		div_general.edt_salePlcNm.SetFocus();
		return false;
	}

	if(uf_isNull(sSaleChgrgId) == true)
	{
		alert(cf_getMessage(MSG_00083, "판매담당자"));
		div_general.cmb_svcChgrgId.SetFocus();
		return false;
	}

	if(uf_isNull(sSvcPlc) == true)
	{
		alert(cf_getMessage(MSG_00083, "개통처"));
		div_general.edt_svcPlcNm.SetFocus();
		return false;
	}

	if(uf_isNull(sSvcChgrgId) == true)
	{
		alert(cf_getMessage(MSG_00083, "개통담당자"));
		div_general.cmb_svcChgrgId.SetFocus();
		return false;
	}	

	// 고객정보

	var sControls  = "div_custInfo.edt_custNm"	// 고객명
	               + ",div_custInfo.cmb_custCl"	// 고객구분
	               + ",div_custInfo.edt_svcNum1,div_custInfo.edt_svcNum2,div_custInfo.edt_svcNum3" // 개통번호
	               + ",div_custInfo.edt_custBizNum"	// 주민번호
	               + ",div_custInfo.edt_svcMgmtNum";	// 서비스관리번호

	var sControlNm = "고객명"
	               + ",고객구분"
	               + ",개통번호,개통번호,개통번호"
	               + ",주민번호"
	               + ",서비스관리번호";

	var arrControl   = Split(sControls , ",");
	var arrControlNm = Split(sControlNm, ",");
	var nCntCntrl = arrControl.length();

	for(var i=0; i<nCntCntrl; i++)
	{
		var oCntrl = eval(arrControl[i]);
		if(uf_isNull(oCntrl.Text) == true || uf_isNull(oCntrl.Value) == true)
		{	// MSG_00083 : (을)를 입력 하여 주시기 바랍니다.
			alert(cf_getMessage(MSG_00083, arrControlNm[i]));
			oCntrl.SetFocus();
			return false;
		}
	}	
}



