/*******************************************************************************
 * @description    :  판매처 - 키 이벤트 
*******************************************************************************/
function div_general_edt_salePlcNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{

	if(nChar == 13)
	{
		var sArg  = "deal_co_cl1="+quote(sf_getDealCoCl1());
			sArg += " deal_status="+quote("4"); 	//옵션 (거래처 상태) - 1: 수납정지 N  , 2: 출고정지 N , 3: 판매정지 N 
			sArg += " pos_agency="+quote(FV_AGENCY_ID); // 옵션	    
			sArg += " eff_dtm="+quote(div_general.cal_saleDtm.Value);
		cf_callDealPop(sArg, div_general, "edt_salePlcNm,edt_salePlc,edt_salePlcUkeyCd,edt_salePlcSubCd,edt_salePlcChannelCd");

		if(uf_isNull(FV_STR_POP_RESULT) == true) return;

		f_getStlPlc();
		f_getSaleChgrgId();
	}
}


