/*******************************************************************************
 * @description    :  저장하기
*******************************************************************************/
function btn_save_OnClick(obj)
{

	if(ds_masterInfo.GetUpdate() == false && ds_supSvcSelect.GetUpdate() == false)
	{
		alert(cf_getMessage(MSG_00071,""));
		return;
	}
	if(ds_supSvcSelect.rowcount == 0)
	{
		alert(cf_getMessage(MSG_00071,""));
		return;
	}	

	if(f_validSave() == false) return;

	if(Confirm( "저장하시겠습니까?" ) == false) return;

	ds_masterInfo.SetColumn(0, "FLAG", "NEW");
	ds_masterInfo.SetColumn(0, "SALE_DTM", f_getDate("SALE"));
	ds_masterInfo.SetColumn(0, "SVC_DTM", f_getDate("SVC"));
	ds_masterInfo.SetColumn(0, "SVC_NUM", f_getSvcNum());

	// 트랜잭션 설정
	var sSvcID = "svcSave";
	var sInDs  = "master=ds_masterInfo itemList=ds_supSvcSelect:u";
	var sOutDs = "";
	var sArg   = "sktst.sal.msl.MSS#saveSupProd";
//alert(ds_supSvcSelect.SaveXml());
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);	
}




