var FV_RUNNING = false;
function SALSMG02500_OnLoadCompleted(obj)
{
	div_search.cal_saleDt.value = Today();	
	SetTimer(1000, 1000*60*10);
}

/*******************************************************************************
* 기능명 : 콤보박스
*******************************************************************************/
function f_setCombo()
{
	// 공통 코드를 위한 데이타셋 설정
	cf_setCommonCodeDsEnv();

//	cf_setCommonCode("div_search.cmb_slCl"    , "ZSAL_C_00260", "전체");	// 영업구분
	cf_checkCommonCode();

//	cf_setInitDataForCmb(div_search.cmb_slNetCd, "전체");
}


/*******************************************************************************
* 함수명 : f_callBack 
* 기능명 : 트랜잭션 콜백 
* 파라미터
1. sSvcID  : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{	
	if(sSvcID == "svcSearch")
	{		
	//trace(ds_disMnth.SaveXML());
		var nCols = grd_prMnyList.GetCellCount("body");
		for(var i = 2; i < nCols; i++)
		{	// 추정손익 구하기
			var nSum = toNumber(replace(grd_prMnyList.GetCellText("BODY", 3, i), ",","")) - toNumber(replace(grd_prMnyList.GetCellText("BODY", 7, i), ",",""));
			grd_prMnyList.SetCellProp("Summ", i-1, "VALUE", nSum);
		}

	}
}

/*******************************************************************************
* 기능명 : 조회 유효성
*******************************************************************************/
function f_isValidSearch(bMsg)
{
	if(uf_isNull(div_search.cal_saleDt.Value) == true)
	{
		if(bMsg == true)
		{
			alert(cf_getMessage(MSG_00083, "조회일자"));
			div_search.cal_saleDt.SetFocus();
		}
		return false;
	}
	return true;
}

function f_procSearch()
{
	if(FV_RUNNING == true) return;
	// 조회
	cf_setParam(ds_condition, "date"   , div_search.cal_saleDt.Value); 
	//cf_setParam(ds_condition, "org_id" , div_search.edt_orgId.Text); 

	var sSvcID  = "svcSearch";
	var sInDs   = "nc_input_fieldDs=ds_condition";  
	var sOutDs  = "ds_prMnyList=PrMnyList ds_mnthList=MnthList ds_todayList=TodayList ds_disMnth=DisMnthList";
	var sArg    = "sktst.sal.SMG#getTodayMntResult";
	http.Sync = true;
	FV_RUNNING = true;
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);
	FV_RUNNING = false;
	http.Sync = false;
}


/*******************************************************************************
* 함수명 : btnSearch_OnClick
* 기능명 : 조회 Button을 Click 했을 경우
*******************************************************************************/
function div_search_btn_Search_OnClick(obj)
{	
	if(f_isValidSearch(true) == false) return;

	f_procSearch();
}

/*******************************************************************************
 * @description    : 엑셀 출력 - 이벤트
