/*******************************************************************************
* 함수명 : df_auth
* 기능명 : 사용자의 권한별 Button 제어
*******************************************************************************/
function f_auth()
{
	f_getSession();

	uf_enableAgencyByAuth("div_search" ,"edt_AgencyCdNm,edt_AgencyCd,btn_Agency","edt_AgencyUkeyCd");
	uf_enableDealByAuth("div_search" ,"edt_dealNm,edt_dealCd,btn_dealCo","edt_dealUkey");
}

