function grd_SaleReport_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sScreenId;

	var sArg  = "";
	    sArg += " sale_dmt_from="    + quote(div_search.cal_saleDtmFrom.Value);
	    sArg += " sale_dmt_to="      + quote(div_search.cal_saleDtmTo.Value);
	    sArg += " agency_cd="        + quote(div_search.edt_AgencyCd.Text);
	    sArg += " agency_cd_nm="     + quote(div_search.edt_AgencyCdNm.Text);
	    sArg += " agency_ukey_cd="   + quote(div_search.edt_AgencyUkeyCd.Text);
	    sArg += " stl_plc_nm="       + quote(div_search.edt_dealNm.Text);
	    sArg += " stl_plc="          + quote(div_search.edt_dealCd.Text);
	    sArg += " stl_plc_ukey_cd="  + quote(div_search.edt_dealUkey.Text);
	    sArg += " sale_dty_typ="     + quote(div_search.cmb_saleDtlTyp.Value);	    
	    sArg += " ds_net_cd="        + quote(div_search.cmb_DsNet.Value);
	    sArg += " prod_cl="          + quote(div_search.cmb_ProdCl.Value);
	    sArg += " mdl_cd="           + quote(div_search.edt_prodCd.Text);
	    sArg += " mdl_cd_nm="        + quote(div_search.edt_prodNm.Text);

	if(grd_list.Format == "Default")
	{	    
		if(nCell >= 4 && nCell <= 7)
		{	// 일반판매세부내역
			sScreenId = "100000298";
		}
		else if(nCell >= 8 && nCell <= 10)
		{	// 공기기판매세부내역
			sScreenId = "100000299";
		}
	}
	else
	{
		if(nCell >= 3 && nCell <= 16)
		{	// 일반판매세부내역
			sScreenId = "100000298";
		}
		else if(nCell >= 7 && nCell <= 19)
		{	// 공기기판매세부내역
			sScreenId = "100000299";
		}	
	}

	gf_OpenWindow( sScreenId, sArg);
}	


function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sScreenId;

	var sSlCl     = ds_list.GetColumn(nRow, "SL_CL");
	var sSlNetCd  = ds_list.GetColumn(nRow, "SL_NET_CD");
	var sStlPlc   = ds_list.GetColumn(nRow, "STL_PLC");
	var sStlPlcNm = ds_list.GetColumn(nRow, "sTL_PLC_NM");

	var sArg  = "";
	    sArg += " sale_dmt_from="    + quote(div_search.cal_saleDtmFrom.Value);
	    sArg += " sale_dmt_to="      + quote(div_search.cal_saleDtmTo.Value);
	    sArg += " agency_cd="        + quote(div_search.edt_AgencyCd.Text);
	    sArg += " agency_cd_nm="     + quote(div_search.edt_AgencyCdNm.Text);
	    sArg += " agency_ukey_cd="   + quote(div_search.edt_AgencyUkeyCd.Text);
	    sArg += " stl_plc_nm="       + quote(div_search.edt_dealNm.Text);
	    sArg += " stl_plc="          + quote(sStlPlc);
	    sArg += " stl_plc_ukey_cd="  + quote(sStlPlcNm);
	    sArg += " sale_dty_typ="     + quote(div_search.cmb_saleDtlTyp.Value);	    
	    sArg += " ds_net_cd="        + quote(div_search.cmb_DsNet.Value);
	    sArg += " mdl_cd="           + quote(div_search.edt_prodCd.Text);
	    sArg += " mdl_cd_nm="        + quote(div_search.edt_prodNm.Text);
	    sArg += " sl_cl="            + quote(sSlCl);
	    sArg += " sl_net_cd="        + quote(sSlNetCd);


	if(div_search.rdo_SearchCl.index == 0)
	{
		if(nCell >= sf_getIndexByGridHeader(grd_list, "신규") 
			&& nCell <= sf_getIndexByGridHeader(grd_list, "소계") )
		{	// 일반판매세부내역
			sScreenId = "100000298";
		}
		else if(nCell >= sf_getIndexByGridHeader(grd_list, "단말기") 
			&& nCell <= sf_getIndexByGridHeader(grd_list, "기타상품") )
		{	// 공기기판매세부내역
			sScreenId = "100000299";
		}
	}
	else
	{
		if(nCell >= sf_getIndexByGridHeader(grd_list, "010신규") 
			&& nCell <= sf_getIndexByGridHeader(grd_list, "일반판매소계") )
		{	// 일반판매세부내역
			sScreenId = "100000298";
		}
		else if(nCell >= sf_getIndexByGridHeader(grd_list, "단말기") 
			&& nCell <= sf_getIndexByGridHeader(grd_list, "기타상품") )
		{	// 공기기판매세부내역
			sScreenId = "100000299";
		}	
	}	

	gf_OpenWindow( sScreenId, sArg);	
}



/*******************************************************************************
 * @description    : 영업구분 변경 이벤트
*******************************************************************************/
function div_search_cmb_slCl_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var sValue = div_search.cmb_slCl.Value;
	if(uf_isNull(sValue) == false)
	{
		cf_setCommonCodeDsEnv();
		cf_setCommonCode("div_search.cmb_slNetCd"    , "ZPOL_C_001" + sValue , "전체");	// 영업구분
		cf_checkCommonCode(); 
	}
	else
	{		
		Object(div_search.cmb_slNetCd.InnerDataset).DeleteAll();
		cf_setInitDataForCmb(div_search.cmb_slNetCd, "전체");	
	}
}


/*******************************************************************************
 * @description : 정산처  팝업 
 ******************************************************************************/
function div_search_btn_dealCo_OnClick(obj)
{
	sf_clearControls("div_search.edt_dealCd,div_search.edt_dealUkey");

	var sPosAgency = div_search.edt_agencyCd.Text;

	if( uf_isNull(sPosAgency) == true)
	{
		alert(cf_getMessage(MSG_00083, "대리점"));
		return;
	}

	var sArg  = "deal_co_cl1="+quote(sf_getDealCoCl1());
		sArg += " pos_agency="+quote(sPosAgency); // 옵션	    
		sArg += " only_stl_plc='Y'";

	cf_callDealPop(sArg, div_search, "edt_dealNm,edt_dealCd,edt_dealUkey");
}	


/*******************************************************************************
 * @description    : 정산처 key down - 이벤트
*******************************************************************************/
function div_search_edt_dealNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == "13")
	{
		var sPosAgency = div_search.edt_agencyCd.Text;

		if( uf_isNull(sPosAgency) == true)
		{
			alert(cf_getMessage(MSG_00083, "대리점"));
			return;
		}

		var sArg  = "deal_co_cl1="+quote(sf_getDealCoCl1());
			sArg += " pos_agency="+quote(sPosAgency);
			sArg += " only_stl_plc='Y'";

		cf_callDealPop(sArg, div_search, "edt_dealNm,edt_dealCd,edt_dealUkey");
	}
}

function div_search_edt_dealNm_OnCharChanged(obj,strPreText,strPostText)
{
	sf_clearControls("div_search.edt_dealCd,div_search.edt_dealUkey");
}

function div_search_cmb_prodCl_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	sf_clearControls("div_search.edt_prodNm,div_search.edt_prodCd");
}



/*******************************************************************************
 * @description    : 판매담당자 리스트 조회
*******************************************************************************/
function f_getSaleChgrgId()
{

	var sAgency = div_search.edt_agencyCd.Text;

	//if( uf_isNull(sAgency) == true) return;

	ds_Condition.Clear();

	// 넘길 파라메터 설정
	cf_setParam(ds_Condition, "agency_cd", sAgency); 

	// 트랜잭션 설정
	var sSvcID = "svcSaleChgrgId";
	var sInDs  = "nc_input_fieldDs=ds_Condition";
	var sOutDs = "ds_saleChgrgId=ds_ReqUser";
	var sArg   = "sktst.sal.SCO#getUserList";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}
