	cf_exportExcel(grd_list);
}


/*******************************************************************************
 * @description    : 화면 로딩시
*******************************************************************************/
function f_init(oDsArg)
{

	http.Sync = true;
	f_setCombo();

	if(sf_getArguments("sale_dtm_from", oDsArg) != "")
	{
		div_search.cal_ifDtmFrom.Value   = sf_getArguments("sale_dtm_from",oDsArg) ;
		div_search.cal_ifDtmTo.Value     = sf_getArguments("sale_dtm_to",oDsArg) ;
		div_search.edt_agencyCdNm.Text   = sf_getArguments("agency_nm",oDsArg) ;
		div_search.edt_agencyUkeyCd.Text = sf_getArguments("agency_ukey",oDsArg) ;
		div_search.edt_agencyCd.Text     = sf_getArguments("agency_cd",oDsArg) ;
		div_search.edt_SalePlcNm.Text     = sf_getArguments("sale_plc_nm",oDsArg) ;
		div_search.edt_SalePlcUkeyCd.Text = sf_getArguments("sale_plc_ukey",oDsArg) ;
		div_search.edt_SalePlcSubCd.Text  = sf_getArguments("sale_plc_sub",oDsArg) ;
		div_search.edt_SalePlcChannelCd.Text = sf_getArguments("sale_plc_channel",oDsArg) ;
		div_search.edt_SalePlc.Text       = sf_getArguments("sale_plc",oDsArg) ;

		sf_setComboValue(div_search.cmb_dsNetCd, sf_getArguments("ds_net_cd",oDsArg));
		sf_setComboValue(div_search.cmb_gubun, sf_getArguments("ds_net_cd_cl",oDsArg));
		div_search.edt_svcMgmtNum.Text       = sf_getArguments("svc_mgmt_num",oDsArg) ;
		div_search.cmb_slCl.Value            = sf_getArguments("sl_cl",oDsArg) ;

		div_search_cmb_dsNetCd_OnChanged();
		f_getSession();		
		div_search_btn_search_OnClick();

	}
	else
	{		
		div_search.cal_ifDtmFrom.value = Today();	//addDate(Today(), -7 );
		div_search.cal_ifDtmTo.value = Today();	
	}
	http.Sync = false;
	f_setAuth();	

}



/*******************************************************************************
 * @description    :  대리점 / 판매처 권한에 따라 컨트롤 제어하기
*******************************************************************************/
function f_setAgencyDealPlcCntrlBySession(sDiv, sAgencyControls, sDealControls)
{
	var sAgencyArr = Split(sAgencyControls,",");
	var sDealArr   = Split(sDealControls,",");


	// 도매담당자, 소개담당자, 온라인소매, 온라인도매담당자
	var nAuthCode = uf_getAuthCode();

	if(nAuthCode == 1)	// 관리자
	{	// 대리점 또는 관리자
		if(uf_isNull(sAgencyControls) == false)
		{
			eval(sDiv + "." + sAgencyArr[0]).Enable = false;
			eval(sDiv + "." + sAgencyArr[1]).Enable = false;
			eval(sDiv + "." + sAgencyArr[2]).Enable = false;
			eval(sDiv + "." + sAgencyArr[3]).Enable = false;
		}
	}
	if(nAuthCode == 2)
	{	// 직영점 또는 외부인
		if(uf_isNull(sAgencyControls) == false)
		{
			eval(sDiv + "." + sAgencyArr[0]).Enable = false;
			eval(sDiv + "." + sAgencyArr[1]).Enable = false;
			eval(sDiv + "." + sAgencyArr[2]).Enable = false;
			eval(sDiv + "." + sAgencyArr[3]).Enable = false;
		}

		if(uf_isNull(sDealControls) == false)
		{
			eval(sDiv + "." + sDealArr[0]).Enable = false;
			eval(sDiv + "." + sDealArr[1]).Enable = false;
			eval(sDiv + "." + sDealArr[2]).Enable = false;
			eval(sDiv + "." + sDealArr[3]).Enable = false;
			eval(sDiv + "." + sDealArr[4]).Enable = false;
			eval(sDiv + "." + sDealArr[5]).Enable = false;
		}		                        
	}		

}


/*******************************************************************************
 * @description    : 권한 설정
*******************************************************************************/
function f_setCombo()
{
	cf_setCommonCodeDsEnv();

	cf_setCommonCode("ds_errorCl" , "ZSAL_C_00230");	// 오류구분
	cf_setCommonCode("ds_slCl"    , "ZSAL_C_00260");	//
	cf_setCommonCode("ds_dsNetCd"       , "ZPOL_C_00110");	// 유통망
	cf_setCommonCode("ds_AgrmtPrdCd"    , "ZPOL_C_00230");	//
	cf_setCommonCode("div_search.cmb_dsNetCd" , "ZPOL_C_00110", "전체");	// 유통망
	cf_setCommonCode("div_search.cmb_slCl" , "ZSAL_C_00260", "전체");   //영업구분	
	cf_setInitDataForCmb(div_search.cmb_gubun, "전체");
	cf_checkCommonCode(); 	
}



/*******************************************************************************
 * @description    : 권한 설정
*******************************************************************************/
function f_setAuth()
{
	f_getSession();

	uf_enableAgencyByAuth("div_search" ,"edt_agencyCdNm,edt_agencyCd,btn_agency","edt_agencyUkeyCd");
	uf_enableDealByAuth("div_search" ,"edt_salePlcNm,edt_salePlc,btn_payPlc","edt_agencyUkeyCd,edt_salePlcSubCd,edt_salePlcChannelCd");
	sf_enableSlCLByAuth(div_search.cmb_slCl);
}

/*******************************************************************************
 * @description    : 글로벌 섹션 정보
*******************************************************************************/
function f_getSession()
{
	FV_USER_ID        = gds_session.GetColumn(0, "loginid");	// 사용자ID
	FV_USER_NM        = gds_session.GetColumn(0, "usernm");		// 사용자명

	FV_ORG_ID         = gds_session.GetColumn(0, "orgid");				// 소속 
	FV_ORG_NM         = gds_session.GetColumn(0, "orgnm");				// 소속명
	FV_ORG_CL         = gds_session.GetColumn(0, "orgcl");				// 소속구분
	FV_AGENCY_ID      = gds_session.GetColumn(0, "posAgencyId");        // 소속 대리점ID
	FV_AGENCY_NM      = gds_session.GetColumn(0, "posAgencyNm");        // 소속 대리점명
	FV_AGENCY_UKEY_ID = gds_session.GetColumn(0, "posAgencyUkeyId");    // 소속 대리점UKEY ID	

	FV_ORG_AREA_ID            = gds_session.GetColumn(0, "orgareaid");			// 근무지 ID
	FV_ORG_AREA_NM            = gds_session.GetColumn(0, "orgareanm");			// 근무지명
	FV_ORG_AREA_POS_AGENCY_CD = gds_session.GetColumn(0, "orgareaposagencycd");	// 근무지 
	FV_ORG_AREA_UKEY_ID       = gds_session.GetColumn(0, "orgareaukeyid");      // 근무지ukeyid
	FV_ORG_AREA_SUB_CD        = gds_session.GetColumn(0, "orgareaukeysubcd");   // 근무지subcd
	FV_ORG_AREA_CHANNEL_CD    = gds_session.GetColumn(0, "orgareaukeychannelcd");  // 근무지 channelcd	
	FV_USER_GRP               = gds_session.GetColumn(0, "usergrp");			// 유저그룹
	FV_ORG_AREA_CL1           = gds_session.GetColumn(0, "orgareacl1");			// 근무지 구분
	FV_DUTY                   = gds_session.GetColumn(0, "duty");	   		    // 직책	


}


/*******************************************************************************
 * @description    :  검색전 유효성 검사
 *******************************************************************************/
function f_validSearch()
{
	if(uf_isNull(div_search.edt_agencyCd.Text) == true)
	{
		alert(cf_getMessage(MSG_00083, "대리점"));
		div_search.edt_agencyCd.SetFocus();
		return false;
	}
	return sf_isValidDateFromTo(div_search.cal_ifDtmFrom,div_search.cal_ifDtmTo, 0, true);
}





