function f_validChgDealInfo()
{
	var nCntRow = ds_list.GetRowCount();
	var sMsg = "";
	var bErr = false;

	http.Sync = true;

	for(var i=0; i < nCntRow; i++)
	{
		if(ds_list.GetColumn(i, "CHK") == "1")
		{
			var sProcDt        = ds_list.GetColumn(i, "proc_dt");
			var sProcTm        = ds_list.GetColumn(i, "proc_tm");
			var sUkeyAgencyCd  = ds_list.GetColumn(i, "ukey_agency_cd");
			var sUkeySubCd     = ds_list.GetColumn(i, "ukey_sub_cd");
			var sUkeyChannelCd = ds_list.GetColumn(i, "UKEY_CHANNEL_CD");			
			var sSalePlc       = ds_list.GetColumn(i, "SALE_PLC");
			var sSalePlcNm     = ds_list.GetColumn(i, "SALE_PLC_NM");

			if(uf_IsNull(sSalePlc) == true)
			{
				sSalePlc = sf_getDealCoCdByUkey("ds_input", "ds_output"
							, sProcDt, sProcTm, sUkeyAgencyCd, sUkeySubCd, sUkeyChannelCd);
				if(uf_IsNull(sSalePlc) == true)
				{
					bErr = true;
					sMsg += sUkeyAgencyCd +"/"+sUkeySubCd +"/"+ sUkeyChannelCd + " 거래처 오류입니다. \r\n";
					continue;
				}
			}

			if(sf_isChgDealInfo("ds_input", "ds_output", sSalePlc, (sProcDt + sProcTm) ) == false)
			{
				bErr = true;
				sMsg += sProcDt + " 판매일이후 " + sSalePlcNm + " 거래처 정보가 변경되었습니다. \r\n";
			}	
		}
	}

	http.Sync = false;
	if(bErr == true)
	{
		alert(sMsg);
		return false;
	}
	return true;
}

/*******************************************************************************
 * @description    : 저장
*******************************************************************************/
function btn_save_OnClick(obj)
{
	if(ds_list.GetUpdate() == false || f_isChangeList() == false)
	{
		alert(cf_getMessage(MSG_00071,""));
		return;
	}


	if(f_validRmks(ds_list.currow) == false) return;

	if(f_validChgDealInfo() == false) return;

	if(Confirm("저장하시겠습니까?") == false) return;

	createExceptHst();

	ds_input.Clear();
	cf_setParam(ds_input, "ps_apnd_ynz" , "N");  // 제외 상태로 설정
	cf_setParam(ds_input, "table_nm" , "TSAL_DSNET_CHG_IF"); 

	var sSvcID = "svcSave";
	var sInDs  = "nc_input_fieldDs=ds_input List=ds_list:u ExceptList=ds_exceptHst";
	var sOutDs = "";
	var sArg   = "sktst.sal.SCO#updatePsApndYnList";	

	//alert(ds_exceptHst.SaveXML());
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}




/*******************************************************************************
 * @description    : 폼 닫기 이벤트
*******************************************************************************/
function SALSUI01600_OnUnloadCompleted(obj)
{
	if(ds_list.GetUpdate() == false) return;

	if(f_isChangeList() == true)
	{
		if(confirm("변경된 데이타가 미반영 상태입니다. 이대로 종료 하시겠습니까?") == true)
		{
			return true;
		}
		return false;
	}	
}




/*******************************************************************************
 * @description    :  List 에 변경된 데이터가 있는지 
*******************************************************************************/
function f_isChangeList()
{
	var nRows = ds_list.GetRowCount();	

	for(var i=0; i<nRows;i++)
	{
		if(ds_list.GetColumn(i, "chk") == "1")
			return true;
	}
	return false;
}

/*******************************************************************************
 * @description    :  로우 위치 변경시 비고 입력여부 확인 
*******************************************************************************/
function ds_list_CanRowPosChange(obj,nOldRow,nRow)
{
	return f_validRmks(nOldRow);
}



/*******************************************************************************
 * @description    :  미반영 제외 히스토리 데이터 생성
*******************************************************************************/
function createExceptHst()
{
	var nRows = ds_list.GetRowCount();	
	ds_exceptHst.DeleteAll();
	for(var i = 0 ; i < nRows ; i++)
	{
		if(ds_list.GetColumn(i,"CHK") == "1")
		{
			var nRow = ds_exceptHst.AddRow();
			ds_exceptHst.SetColumn(nRow, "OP_DT"        , ds_list.GetColumn(i, "OP_DT"));
			ds_exceptHst.SetColumn(nRow, "OP_TM"        , ds_list.GetColumn(i, "OP_TM"));
			ds_exceptHst.SetColumn(nRow, "SEQ"          , ds_list.GetColumn(i, "SEQ"));
			ds_exceptHst.SetColumn(nRow, "IF_REC_TYP"   , ds_list.GetColumn(i, "IF_REC_TYP"));
			ds_exceptHst.SetColumn(nRow, "BF_STATUS"    , ds_list.GetColumn(i, "PS_APND_YN"));
			ds_exceptHst.SetColumn(nRow, "AF_STATUS"    , "N");
			ds_exceptHst.SetColumn(nRow, "RMKS"         , ds_list.GetColumn(i, "RMKS"));

			ds_exceptHst.SetColumn(nRow, "SVC_MGMT_NUM" , ds_list.GetColumn(i, "SVC_MGMT_NUM"));
			ds_exceptHst.SetColumn(nRow, "SVC_NUM"      , ds_list.GetColumn(i, "SVC_NUM"));
			ds_exceptHst.SetColumn(nRow, "SALE_PLC"     , ds_list.GetColumn(i, "SALE_CHG_PLC"));
			ds_exceptHst.SetColumn(nRow, "SL_CL"        , ds_list.GetColumn(i, "SL_CL"));

		}
	}
}



function btn_close_OnClick(obj)
{
	Close();
}




function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 0)
	{
		var sDsNetCd   = ds_list.GetColumn(nRow, "DS_NET_CD");
		var sDsNetCdCl = ds_list.GetColumn(nRow, "DS_NET_CHG_CL");
//		trace(sDsNetCd + "/" + sDsNetCdCl);
		if( sDsNetCd == "2")
		{
			if(sDsNetCdCl == "02" || sDsNetCdCl == "03")
			{				
				ds_list.SetColumn(nRow, "CHK", "1");				
				alert("해약/해약 철회 자료는 반영해제 처리를 할 수 없습니다.");				
				return;
			}
		}
	}
}

function div_search_cmb_dsNetCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	cf_setCommonCodeDsEnv();

	var sCode = obj.Value;

	switch(sCode)
	{
	case "1":	// T할부지원
		cf_setCommonCode("div_search.cmb_gubun" , "ZPOL_C_00111", "전체");
		break;

	case "2":
		cf_setCommonCode("div_search.cmb_gubun" , "ZPOL_C_00112", "전체");
		break;

	default:		
		var oDataset = Object(div_search.cmb_gubun.InnerDataset);
		oDataset.DeleteAll();
		cf_setInitDataForCmb(div_search.cmb_gubun, "전체");
		break;
	}

	cf_checkCommonCode(); 		
}
