/*******************************************************************************
 * @description    : 서비스관리번호 또는 개통번호로 판매된 건 조회
*******************************************************************************/
function f_getSvcMgmtNumItem()
{
	//조회 조건
    //ds_input
    cf_setParam(ds_input, "SVC_MGMT_NUM"     , div_search.edt_svcMgmtNum.Text);
	cf_setParam(ds_input, "SVC_NUM"          , div_search.edt_svcNum.Text);

 	var sSvcID = "getSvcMgmtNumItem";
	var sInDs  = "nc_input_fieldDs=ds_input";
	var sOutDs = "ds_svcMgmtNumItem=List";
	var sArg   = "sktst.sal.SUI#getSvcMgmtNumItem";	
	http.Sync = true;
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);   
	http.Sync = false;
}


/*******************************************************************************
 * @description    : 조회 유효성 검사
*******************************************************************************/
function f_validSearch()
{
	if(uf_isNull(div_search.edt_svcMgmtNum.Text) == true
		&& uf_isNull(div_search.edt_svcNum.Text) == true)
	{
		alert("서비스번호 또는 개통번호를 입력하세요.");
		return false;
	}
	return true;
}




/*******************************************************************************
 * @description    :  일반판매 데이터 조회
*******************************************************************************/
function f_getGnrlSaleInfo(sGnrlSaleNo)
{
	//조회 조건
	ds_input.Clear();
	cf_setParam(ds_input, "GNRL_SALE_NO" ,sGnrlSaleNo);

 	var sSvcID = "getGnrlSaleInfo";
	var sInDs  = "nc_input_fieldDs=ds_input";
	var sOutDs = "ds_gnrlSaleInfo=List";
	var sArg   = "sktst.sal.SUI#getGnrlSaleInfo";	

	http.Sync = true;
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);   
	return FV_GNRL_SALE_NO;
	http.Sync = false;	
}


/*******************************************************************************
 * @description    :  사용자정의 취소/복원 대상 인터페이스 데이터 조회
*******************************************************************************/
function f_getRestoreSaleIf()
{
	var sSvcDtm = ds_gnrlSaleInfo.GetColumn(0, "SVC_DTM");
	var sProcDt = sSvcDtm.substr(0,8);
	var sProcTm = sSvcDtm.substr(8,4);
	//조회 조건
	ds_input.Clear();
	cf_setParam(ds_input, "PROC_DT" ,sProcDt);
	cf_setParam(ds_input, "PROC_TM" ,sProcTm);
	cf_setParam(ds_input, "SVC_MGMT_NUM" ,FV_SVC_MGMT_NUM);

 	var sSvcID = "getRestoreSaleIf";
	var sInDs  = "nc_input_fieldDs=ds_input";
	var sOutDs = "ds_restoreSaleIf=List";
	var sArg   = "sktst.sal.SUI#getRestoreSaleIf";	

	http.Sync = true;
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);   
	http.Sync = false;	
}


/*******************************************************************************
 * @description    : 콜백
 * @param sSvcID   : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{	

	switch(sSvcID)
	{
	case "getSvcMgmtNumItem":

		break;

	case "getGnrlSaleInfo":		
		var sGrnlSaleChgSeq = ds_gnrlSaleInfo.GetColumn(0, "GNRL_SALE_CHG_SEQ");
		var sSaleChgHst     = ds_gnrlSaleInfo.GetColumn(0, "SALE_CHG_HST_CL");
		var sSaleDtm        = ds_gnrlSaleInfo.GetColumn(0, "SALE_DTM");
		var sSvcDtm         = ds_gnrlSaleInfo.GetColumn(0, "SVC_DTM");
		FV_SVC_MGMT_NUM     = ds_gnrlSaleInfo.GetColumn(0, "SVC_MGMT_NUM");

		var sCustNm         = ds_gnrlSaleInfo.GetColumn(0, "CUST_NM");
		var sCustBizNum     = ds_gnrlSaleInfo.GetColumn(0, "CUST_BIZ_NUM");
		var sSvcNum         = ds_gnrlSaleInfo.GetColumn(0, "SVC_NUM");

		div_custInfo.edt_custNm.Text     = sf_getMasking(sCustNm,"CustNm");
		div_custInfo.edt_custBizNum.Text = sf_getMasking(sCustBizNum,"custbiznum");
		div_custInfo.edt_svcNum.Text     = sf_getMasking(sSvcNum,"svcnum");

		div_dealCoInfo.cal_saleDtm.Value  = sSaleDtm.substr(0,8);
		div_dealCoInfo.edt_saleDtmHh.Text = sSaleDtm.substr(8,2);
		div_dealCoInfo.edt_saleDtmMm.Text = sSaleDtm.substr(10,2);
		div_dealCoInfo.cal_svcDtm.Value   = sSvcDtm.substr(0,8);
		div_dealCoInfo.edt_svcDtmHh.Text  = sSvcDtm.substr(8,2);
		div_dealCoInfo.edt_svcDtmMm.Text  = sSvcDtm.substr(10,2);

		if(toNumber(sGrnlSaleChgSeq) > 1)
		{	// 최초 판매등록이 아닐 경우 
			alert("최초 판매등록 건이 아닙니다.");
			FV_GNRL_SALE_NO = false;
			return;
		}
		if(sSaleChgHst == "12")
		{	// 판매처변경
			alert("판매처 변경 이력입니다.");
			FV_GNRL_SALE_NO = false;
			return;
		}
		else if(sSaleChgHst == "13")
		{	// 약정변경
			alert("약정변경 이력입니다.");
			FV_GNRL_SALE_NO = false;
			return;
		}

		FV_GNRL_SALE_NO = true;
		break;


	case "getRestoreSaleIf":

		break;


	default:
		break;	
	}	
}

/*******************************************************************************
 * @description    :  저장 유효성 검사
*******************************************************************************/
function f_validSave()
{
	var nCntRows = ds_restoreSaleIf.GetRowCount();
	var nCntChk  = 0;
	for(var i = 0;i<nCntRows;i++)
	{
		if(ds_restoreSaleIf.GetColumn(i, "CHK") == "1")
		{
			nCntChk++;
		}
	}
	if(nCntChk ==0)
	{
		alert(cf_getMessage(MSG_00071,""));
		return false;
	}
	else if(nCntChk > 1)
	{
		alert("2건이상 선택할 수 없습니다.");
		return false;
	}
	return true;
}


/*******************************************************************************
 * @description    :  저장 - 이벤트
*******************************************************************************/
function btn_save_OnClick(obj)
{
	if(f_validSave() == false) return;

	if(Confirm("판매취소전문을 생성하시겠습니까?") == false) return;
	f_setCancel();

	if(Confirm("복원전문을 생성하시겠습니까?") == false) return;
	f_setRestore();
}

function f_saveTransaction()
{
 	var sSvcID = "saveInterface";
	var sInDs  = "nc_input_fieldDs=ds_interface";
	var sOutDs = "";
	var sArg   = "sktst.sal.SUI#saveGnrlSaleInfo";	

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);   
}

/*******************************************************************************
 * @description    :  사용자 정의 취소 전문데이터 설정
*******************************************************************************/
function f_setCancel()
{
	var nRow = ds_restoreSaleIf.FindRow("CHK", "1");

	var sGubun = "ZC";

	ds_interface.DeleteAll();	
	var nAddedRow = ds_interface.AddRow();

	ds_interface.CopyRow(nAddedRow, ds_restoreSaleIf, nRow);
	ds_interface.SetColumn(nAddedRow, "IF_REC_TYP",sGubun);
	ds_interface.SetColumn(nAddedRow, "PS_APND_YN", "N");
	f_setDataTime(sGubun);
	f_saveTransaction();
}



/*******************************************************************************
 * @description    :  사용자 정의 복원 전문데이터 설정
*******************************************************************************/
function f_setRestore()
{
	var nRow = ds_restoreSaleIf.FindRow("CHK", "1");
	var sGubun = f_getIfRecType();

	ds_interface.DeleteAll();	
	var nAddedRow = ds_interface.AddRow();

	ds_interface.CopyRow(nAddedRow, ds_restoreSaleIf, nRow);
	ds_interface.SetColumn(nAddedRow, "IF_REC_TYP", sGubun);
	ds_interface.SetColumn(nAddedRow, "PS_APND_YN", "N");
	f_setDataTime(sGubun);
	f_saveTransaction();
}


function f_setDataTime(sGubun)
{
	var nRow  = ds_restoreSaleIf.FindRow("CHK", "1");
	var sDate = ds_restoreSaleIf.GetColumn(nRow, "PROC_DT");
	var sTime = ds_restoreSaleIf.GetColumn(nRow, "PROC_TM");

	var nSecond = f_getSecond2Time( sTime );

	if(sGubun == "ZC") // 사용자정의판매취소
	{
		nSecond += 60 * 1;	// 1분 설정 
	}
	else if(sGubun == "ZA" || sGubun == "ZB")
	{
		nSecond += 60 * 2;	// 2분 설정
	}
	else
	{}

	var sDateTime    = getTime2Second( nSecond );
	var sDateTimeArr = sDateTime.split(":", "webstyle");

	if(sDateTimeArr[0] != "0")
	{
		ds_interface.SetColumn(0, "PROC_DT", AddDate(sDate, sDateTimeArr[0]));
	}
	ds_interface.SetColumn(0, "PROC_TM", sDateTimeArr[1] + sDateTimeArr[2] + sDateTimeArr[3]);
}

/*******************************************************************************
 * @description    :  사용자 정의 전문유형 반환
*******************************************************************************/
function f_getIfRecType()
{
	var retValue = "";
	var nRow = ds_restoreSaleIf.FindRow("CHK", "1");

	var sIfRecTyp = ds_restoreSaleIf.GetColumn(nRow, "IF_REC_TYP");
	var sNew = "21,22,24,91,93";
	var sChg = "25,26,27,28,65,66";

	if(sNew.indexOf(sIfRecTyp) != -1)
	{
		retValue = "ZA";
	}
	if(sChg.indexOf(sIfRecTyp) != -1)
	{
		retValue = "ZB";
	}
	return retValue;
}

/*******************************************************************************
 * @description    :  시간을 초로 변환
*******************************************************************************/
function f_getSecond2Time( sTime )
{
	var nHours   = toNumber( sTime.substr(0, 2)) * 60 * 60;
	var nMinuts  = toNumber( sTime.substr(2, 2)) * 60;
	var nSeconds = toNumber( sTime.substr(4, 2));

	return nHours + nMinuts + nSeconds;
}


function getTime2Second( strSeconds )
{
	var seconds = toNumber( strSeconds );
	var days = seconds / (60 * 60 * 24);	// 86400으로 나눈 몫이 일수
	var hours = seconds / (60 * 60);		// 3600으로 나눈 몫이 시간

	seconds = seconds % (60 * 60);			// 3600으로 나눈 나머지가 초

	var minutes = seconds / 60;				// 60으로 나눈 몫이 분

	seconds = seconds % 60;					// 60으로 나눈 나머지가 초

	return ParseInt(days) + ":" + ParseInt(hours) + ":" + ParseInt(minutes) + ":" + ParseInt(seconds);
}






/*******************************************************************************
 * @description    :  조회 - 이벤트 
*******************************************************************************/
function div_search_btn_search_OnClick(obj)
{
	if(f_validSearch() == false) return;

	f_getSvcMgmtNumItem();

	ds_list.ClearData();
	ds_list.AddRow();

	var nCntRow = ds_svcMgmtNumItem.GetRowCount();

	if(nCntRow == 1)
	{
		ds_list.SetColumn(0, "GNRL_SALE_NO", ds_svcMgmtNumItem.GetColumn(0, "GNRL_SALE_NO"));
	}
	else if(nCntRow > 1)
	{
		var sArg = "row=" + quote("0");
		dialog("SAL::SALSCO01800.xml", sArg, "800","446");	
	}
	else
	{
		ds_gnrlSaleInfo.DeleteAll();
		ds_restoreSaleIf.DeleteAll();
		return;
	}

	var sGnrlSaleNo = ds_svcMgmtNumItem.GetColumn(0, "GNRL_SALE_NO");

	if(f_getGnrlSaleInfo(sGnrlSaleNo) == false) return;
	f_getRestoreSaleIf();
}
